body#gatewaypage, body#indexpage, body#topicpage, body#reviews, body#docservsgate, body#docservs2 /* Will only style body attributes that have one of these IDs. Add more as neeeded */{color:black;font-family:Verdana, Arial, Helvetica, Geneva, sans-serif;/* IE/Win needs this 'base' font-size */font-size:100.01%;background-color:#fff;margin:0;padding:80px 0 0}
/* upgrade your browser message */
#ahem{display:none}

/*<agl.folder "Basic Elements">*/
p{margin-top:0px;margin-bottom:10px}
td{font-family:Verdana, Arial, Helvetica, Geneva, sans-serif}
h1{font-size:185%;margin:10px 0 10px 0;line-height:1.2;/* IE/Mac needs width. Whatever. */width:550px}
/* Commented backslash hack for IE/Mac. \*/
h1{margin-top:0}
/* Recover from backslash hack */
h2{font-size:110%;margin:10px 0 2px 0}
h3{font-size:100%;margin:0}
h4{color:#333;font-size:100%;margin:0 0 5px}
h4 a{font-weight:normal}
h5{color:#333;font-size:90%;margin:0 0 2px}
cite{font-style:normal}
blockquote{margin:0;padding:0}
ul{margin:-5px 0 10px}
a:link{color:#c00}
a:visited{color:#ac4e4e}
a:hover{color:red;text-decoration:none}
dt{font-weight:bold;margin:0}
dd{margin:0 0 5px}
/* for bulleted lists nested inside numbered lists */
ol ul{margin:5px 0 0 2px}
ol ul li{margin:0 0 5px;background:url("/web/20050206231317im_/http://www.adobe.com/images/ul_bullet.gif") no-repeat 0 0;padding:0 0 0 7px}
/*</agl.folder>*/

/*<agl.folder "Elements with class attributes ">*/
ul.introlinks{margin-bottom:10px}
ul.introlinks li{width:245px;float:left;margin:0 20px 5px 0}
ul.anchorlinks /* IE5/Win needs this */{background-image:none}
ul.anchorlinks li{background-image:url("/web/20050206231317im_/http://www.adobe.com/enterprise/images/anchlink.gif");background-repeat:no-repeat;background-position:0 0.5em;margin-bottom:0;padding-bottom:5px;/* IE5/Win needs these */text-indent:-12px;padding-left:5px;voice-family:"\"}\"";voice-family:inherit;text-indent:0;padding-left:12px}
html>body ul.anchorlinks li{text-indent:0;padding-left:12px}
/* class="first" */
p.first, #topicpage #content #topiclearnmore p.first{padding-top:10px}
#maincontent h2.first, #sidebar h2.first{background-image:none;padding:4px 10px}
#topicpage blockquote.first{background-image:none;margin:0;padding:0}
/* class="last" */
#content .last, #maincontent p.last{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/lastdot.gif") bottom left repeat-x;margin:0;padding:0 10px 10px}
#maincontent ul, #maincontent ul.last{padding:10px}
#indexpage #intro #featured p.last, #indexpage #intro #featured2 p.last{margin-bottom:0}
#reviews #maincontent div.last{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/lastdot.gif") bottom left repeat-x;margin:0;padding:0 0 10px}
/*</agl.folder>*/

/*<agl.folder "Global Enterprise pages ">*/
#calltoaction, #gatewaypage.product #calltoaction{display:block;position:relative;top:0;height:20px}
#gatewaypage #calltoaction, #docservsgate #calltoaction{top:10px}
#calltoaction img{margin:0 0 0 20px;float:right}
#intro{margin:0 0 5px;/* IE/Mac needs a width. Whatever. */width:550px}
#intro h2{color:gray;font-size:115%;margin:10px 0 0 0}
#intro img{float:right;margin:0 0 20px 20px}
#content select{margin:0 5px 0 0}
/* content */
#content{font-size:70%;line-height:135%;background:transparent url(/web/20050206231317im_/http://www.adobe.com/images/css/graybgstrip.gif) top left repeat-y;margin:0;padding:0 0 20px 200px;border:none;/* IE/Win needs this */float:none;/* IE5/Win Box model hack */width:750px;voice-family:"\"}\"";voice-family:inherit;width:550px}
/* IE/Win messes up the generated sidenav when viewport is narrow if #content is floated. */
html>body #content{float:left}
/*</agl.folder>*/

/*<agl.folder "indexpage maincontent and its contextual elements">*/
#maincontent{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/maincontentbg.gif") 0 0 repeat-y;margin:0;padding:0;/* IE/Mac needs a float. Whatever. */float:left;width:550px}
#maincontent h2, #sidebar h2{color:#fff;font-size:100%;line-height:1;background:#999 url("/web/20050206231317im_/http://www.adobe.com/images/css/h2bg.gif") repeat-x;position:relative;z-index:10;margin:0 0 -1px;padding:15px 10px 4px;border-bottom:1px solid #000}
#maincontent h3{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/1dot.gif") top left repeat-x;padding:10px}
#maincontent p{background-color:transparent;margin:0 10px 10px;padding:0}
.runinhead{color:#333;font-weight:bold}
/*</agl.folder>*/

/*<agl.folder "indexpage with side bar ('two-thirds, one-third' layout)">*/
#mainsidewrap{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/mainsidewrap_550.gif") 0 0 repeat-y;margin:10px 0 0;padding:0;float:left;width:550px}
#mainsidewrap #maincontent{background-image:none;margin:0;padding:0 0 10px;float:left;width:384px}
#mainsidewrap #maincontent h3{background-image:none;padding:10px 10px 0}
#sidebar{float:right;width:166px;margin:0}
#sidebar p, #sidebar ul, #sidebar h3{padding-left:10px;padding-right:10px;font-size:xx-small;voice-family:"\"}\"";voice-family:inherit;font-size:x-small}
#sidebar li{line-height:1.2}
#content #mainsidewrap div.last{background:#ccc url("/web/20050206231317im_/http://www.adobe.com/images/css/lastdot.gif") 0 0 repeat-x;margin:1px 0 0;padding:0;height:1px;width:550px;float:left}
/*</agl.folder>*/

/*<agl.folder "Position bucket SSIs">*/
/* Left 'Column' SSIs */
#what, #featured, #featured2, .anchorlinks{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/ssibg_266.gif") 0 0 repeat-y;margin:10px 0;padding:0;float:left;width:266px}
#what, #featured, #featured2{line-height:1.25;font-size:xx-small;voice-family:"\"}\"";voice-family:inherit;font-size:x-small}
/* Right 'Column' SSIs */
#highlights, #learnmore, #indexpage #featured{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/ssibg_265.gif") 0 0 repeat-y;margin:10px 0;padding:0;width:265px;float:right;line-height:1.25;font-size:xx-small;voice-family:"\"}\"";voice-family:inherit;font-size:x-small}
#indexpage #featured{margin-left:15px}
/*</agl.folder>*/

/*<agl.folder "elements in bucket SSIs ">*/
#what h2, #learnmore h2, #highlights h2, #featured h2, #featured2 h2, #topiclearnmore h2, #docservsgate #what h2, .product #what h2{color:#fff;font-size:110%;background-color:#999;position:relative;z-index:10;margin:-1px 0;padding:3px 10px;border-bottom:1px solid black}
#what p, #learnmore p, #highlights p, #featured p, #featured2 p, #topiclearnmore p{margin:0 0 10px;padding-left:10px;padding-right:10px}
#what h3, #learnmore h3, #highlights h3, #featured h3, #featured2 h3, #topiclearnmore h3{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/1dot.gif") top left repeat-x;padding:10px 10px 0}
#what h4, #learnmore h4, #highlights h4, #featured h4, #featured2 h4, #topiclearnmore h4{font-size:100%;margin:0;padding:0 10px}
#what p.linkonly, #featured p.linkonly, #featured2 p.linkonly, #indexpage #featured p.linkonly, #content #featured p.linkonly, #featured p.linkonly, #highlights p.linkonly, #learnmore p.linkonly{margin-top:-7px}
#mainsidewrap #maincontent p.linkonly{margin-top:-10px}
#what h3.linkonly, #learnmore h3.linkonly, #highlights h3.linkonly, #featured h3.linkonly, #featured2 h3.linkonly, #featured2 h3.linkonly, #topicpage #content #topiclearnmore h3.linkonly, h3.linkonly{padding-bottom:10px}
/* if the h3 is the last thing and not followed by a paragraph */
#what h3.linkonly.last, #learnmore h3.linkonly.last, #highlights h3.linkonly.last, #featured h3.linkonly.last, #featured2 h3.linkonly.last, #featured2 h3.linkonly.last, #topicpage #content #topiclearnmore h3.linkonly.last, h3.linkonly.last{padding-top:10px}
#what ul, #learnmore ul, #highlights ul, #featured ul, #featured2 ul, #topiclearnmore ul{padding-left:10px;padding-right:10px;margin-top:0}
/*</agl.folder>*/

/*<agl.folder "'third-level' topic pages">*/
#topicpage #intro h2 {font-size: 1em}
#topicpage h2, #topicpage h3{margin:0 0 5px;color:#000}
#topicpage blockquote{width:350px}
#topicpage blockquote p{width:350px}
#topicpage #content h2.newsubj{width:350px;padding:10px 0 0;margin:20px 0 0}
/* topicpage content */
#topicpage #content{position:relative;z-index:1;/* Box model hack */width:750px;voice-family:"\"}\"";voice-family:inherit;width:550px}
#topicpage #content #intro{width:350px;float:left}
#topicpage #content p, #topicpage #content h2, #topicpage #content h3, #topicpage #content h4, #topicpage #content ul, #topicpage #content ol{width:350px}
#topicpage #content ul li{width:343px}
#topicpage #content li p, #topicpage #content li h2, #topicpage #content li h3{width:330px}
/* For IE/Mac */
html>body#topicpage #content h2, html>body#topicpage #content h3, html>body#topicpage #content h4{margin-right:0;clear:left}
#topicpage #content ul li h3{margin-bottom:0}
#topicpage #content p.diagram, p.diagram{width:550px;margin:5px 0 10px;/* For IE/Mac */clear:left}
/* topicpage intro */
#topicpage #content #intro p, #topicpage #content #intro h2, #topicpage #content #intro h3, #topicpage #content #intro ul, #topicpage #content #intro ol, #topicpage #content #intro li p, #topicpage #content #intro li h2, #topicpage #content #intro li h3{width:auto}
/* topiclearnmore */
#topicpage #content #topiclearnmore{float:right;clear:none;width:165px;background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/ssibg_165.gif") 0 100% repeat-y;padding:0 0 5px;margin:0 0 10px 0;line-height:1.25;font-size:xx-small;voice-family:"\"}\"";voice-family:inherit;font-size:x-small}
/* elements within topiclearnmore */
#topicpage #content #topiclearnmore p, #topicpage #content #topiclearnmore h2, #topicpage #content #topiclearnmore h3, #topicpage #content #topiclearnmore ul, #topicpage #content #topiclearnmore li{padding-left:10px;padding-right:10px;margin-right:0;/* Box model hack */width:165px;voice-family:"\"}\"";voice-family:inherit;width:145px}
#topicpage #content #topiclearnmore li{/* Box model hack */width:158px;voice-family:"\"}\"";voice-family:inherit;width:138px}
#topicpage #content #topiclearnmore h3{margin:0;padding:10px 10px 0;/* Box model hack */width:165px;voice-family:"\"}\"";voice-family:inherit;width:145px}
#topicpage #content #topiclearnmore p{margin:0;padding:0 10px 10px}
#topicpage #content #topiclearnmore h2{margin:-1px 0;color:#fff}
#topicpage blockquote, h2.newsubj, h3.newsubj{background:transparent url("/web/20050206231317im_/http://www.adobe.com/images/css/1dot.gif") top left repeat-x;padding:10px 0 0;margin:20px 0 0}
/*</agl.folder>*/

/*<agl.folder "Document Services 'features' pages (no subnav)">*/
#docservsgate #content, #docservs2 #content{float:left;width:655px;background-image:none;padding:0 0 20px;height:auto;margin:0}
#docservsgate #intro{margin:0 0 0 22px;float:left;width:610px}
/* IE/Win doubles margins */
html>body#docservsgate #intro{margin-left:45px}
#docservsgate #content #what{margin-left:22px}
/* IE/Win doubles margins */
html>body#docservsgate #content #what{margin-left:45px}
#docservsgate h1, #docservs2 h1{margin:10px 0 0 15px}
#docservsgate #content select{margin:0 5px 15px 45px}
#docservsgate .menu{display:block;margin:10px 0 -10px;padding:0;clear:both;vertical-align:top}
html>body#docservsgate .menu{padding-top:10px}
.menu input{border:0;margin:0;padding:0;height:20px;width:21px;line-height:1;vertical-align:top}
.menu img{vertical-align:top}
#docservs2 #content h2, #docservs2 #content h3, #docservs2 #content p, #docservs2 #content ul{width:425px;display:block;margin-left:45px}
#docservs2 #content h2{font-size:125%;margin:0 0 20px 45px}
noscript ul{margin:5px 0 0 5px}
/*</agl.folder>*/

/*<agl.folder "Document Services Product pages (with subnav)">*/
.product h1#h1img.replace{margin-left:-185px;position:relative;z-index:100}
.product .replace em{z-index:5}
.product #subnav, #gatewaypage.product #subnav{margin-top:100px;padding-top:0;background-color:transparent}
/* hide from IE5/Win, but show to IE5.5 and better */
.product #subnav/**/, #gatewaypage.product #subnav/**/{top:72px;/* box model hack */height:562px;voice-family:"\"}\"";voice-family:inherit;height:472px;margin-top:0;background-position:0 90px;padding-top:90px;}
/* Safari needs this */
html>body.product #subnav, html>body#gatewaypage.product #subnav{background-color:#fff}
.product #content{background:transparent url(/web/20050206231317im_/http://www.adobe.com/images/css/graybgstrip_10kh.gif) 0 90px no-repeat}
#topicpage.product #content{position:static;width:750px;/* box model hack */voice-family:"\"}\"";voice-family:inherit;width:550px;}
#topicpage.product h1#h1img.replace{margin-bottom:10px}
.product h2, .product #intro h2, #topicpage.product h2{font-size:125%;margin-bottom:10px}
#topicpage.product #content h2, #topicpage.product #content h3, #topicpage.product #content p, #topicpage.product #content ul, #topicpage.product #content ol{width:350px;display:block;margin-left:0;padding-left:0}
#topicpage.product #content ul li{text-indent:0;width:350px;voice-family:"\"}\"";voice-family:inherit;width:343px;margin-left:0}
#topicpage.product #content ol li{margin:0 0 8px 2.25em;}
#topicpage.product #content ol li ul, #topicpage.product #content ol li ul li{width:320px;margin-left:0}
#topicpage.product #content ul li h3, #topicpage.product #content ul li h4, #topicpage.product #content ul li p{text-indent:0;width:350px;voice-family:"\"}\"";voice-family:inherit;width:343px;margin-bottom:2px}
/*</agl.folder>*/

/*<agl.folder "Image Replacement for header GIFs">*/
/* Image replacement for h1 header GIFs. Links to the actual images should be in the pages themselves (using the #h1img em selector) so that users with slow connections don't download them with this stylesheet. Technique adapted from www.ryznardesign.com/web_coding/image_replacement/ */
.replace{position:relative;/* hide overflow:hidden from mac ie5 \*/ overflow:hidden;/* end hide from mac ie5 */margin:0;padding:0;}
.replace em{display:block;position:absolute;top:0px;left:0px;z-index:30;/*for Opera 5 and 6*/}
.replace a{text-decoration:none;/* ie5.0-5.1.x/mac must have this value or the underline shows through the image. */ /* hide from ie5.x/mac \*/text-decoration:underline;/* end hide from ie5.x/mac */} 
