/* Body alignment display */

.LayoutGrid {
    margin: 0 auto;
}

/* Header display */

.LayoutHeader {
    clear: both;
    position: relative;
}

.LayoutHeaderPath {
    position: relative;
    padding-left: 11px;
}

.LayoutHeaderCell {
    padding: 11px 11px 18px 11px;
	clear: both;
}

.LayoutHeaderRow {
    margin-top: 20px;
    margin-bottom: 10px;
}

.LayoutImage {
    display: block; /* Eliminates extra space around image */
}

.LayoutImageLeft {
    float: left;
    margin-right: 9px;
}

.LayoutImageRight {
    float: right;
    margin-left: 9px;
}

.LayoutMask {
    width:0px;
    height:0px;
    overflow:hidden;
    display: block;
}

/* Clearing floats */

.LayoutBreak {
    clear: both;
}

.LayoutGridBreakAfter,
.LayoutBreakAfter {
    _height: 1em;
    *zoom: 100%;
}

.LayoutGridBreakAfter:after,
.LayoutBreakAfter:after {
    clear: both;
    display: block;
    height: 0;
    content: "\0020";
}

.LayoutGridBreakAfter:after {
    clear: left;
}

.LayoutGridBreak {
    clear: left;
}

/* Stacking elements */

.LayoutCell,
.LayoutSmallCell,
.LayoutBigCell {
    display: block;
}

.LayoutCell,
.LayoutCellVSides,
.LayoutCellTop {
    padding-top: 9px;
}

.LayoutCell,
.LayoutCellVSides,
.LayoutCellBottom {
    padding-bottom: 9px;
}

