/* Horizontal item display */

.LayoutH,
.LayoutHItems,
.LayoutHAlignRight {
    _height: 1em;
    *zoom: 100%;
}

.LayoutHItems:after,
.LayoutH:after,
.LayoutHAlignRight:after {
    content: "\0020";
    display: block;
    clear: both;
    height: 0;
}

.LayoutHItem {
    float: left;
}

.LayoutHItemRight {
    float: right;
}

/* Horizontal items alignment */

.LayoutHAlignBottom {
    vertical-align: bottom;
}

.LayoutHAlignTop {
    vertical-align: top;
}

.LayoutHAlignRight .LayoutHAlignItems {
    float: right;
}

.LayoutCenter {
    display: block;
    margin-left: auto;
    margin-right:auto;
}

.LayoutRight {
    display: block;
    float: right;
}

/* Display elements */
.LayoutHidden {
    display: none;
}

/* Position items absolutely within a specified container */

.LayoutP {
    position: relative;
    z-index: inherit;
    display: block;
}

.LayoutPRight,
.LayoutPLeft,
.LayoutPTop,
.LayoutPBottom,
.LayoutPCenterReg,
.LayoutPCenter,
.LayoutPMiddleReg,
.LayoutPMiddle,
.LayoutPOutsideBottom,
.LayoutPOutsideTop,
.LayoutPOutsideRight,
.LayoutPOutsideLeft {
    display: block;
    position: absolute;
    visibility: visible;
    z-index: inherit;
}

.LayoutPCenterReg,
.LayoutPMiddleReg {
    visibility: hidden;
}

.LayoutPCenterReg {
    left: 50%;
}

.LayoutPCenter {
    right: 50%;
    width: 100%;
}

.LayoutPMiddleReg {
    top: 50%;
}

.LayoutPMiddle {
    bottom: 50%;
    height: 100%;
}

.LayoutPOutsideBottom {
    top: 100%;
}

.LayoutPOutsideTop {
    bottom: 100%;
}

.LayoutPOutsideRight {
    left: 100%;
}

.LayoutPOutsideLeft {
    right: 100%;
}

.LayoutPLeft {
    left: 0;
}

.LayoutPRight {
    right: 0;
}

.LayoutPTop {
    top: 0;
}

.LayoutPBottom {
    bottom: 0;
}

.LayoutPMax {
    width: 100%;
    height: 100%;
}

@media screen and (max-device-width:480px) {
    .LayoutImage {
       max-width: 100%;
    }
}

