    background: url(/web/20110131204055im_/http://wwwimages.adobe.com/homepage/include/style/default/Menu/Drop/icon.png) no-repeat right 9px;
    padding: 0 25px 0 9px;
    color: #FFFFFF;
    font-size: 12px;
    font-weight: bold;
	cursor: pointer;
    line-height: 20px;
	border: 1px solid #376B9A;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	z-index: inherit;
}

.MenuDropName a,
.MenuDropName a:hover,
.MenuDropName a:visited,
.MenuDropName a:visited:hover
{
	color: #FFFFFF !important;
	text-decoration: none;
}

.MenuDropPanel
{
    position: absolute;
    background-color: #ECECEC;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
    border-top: 0px;
	border-right: 1px solid #B0B0B0;
	border-bottom: 1px solid #737373;
	border-left: 1px solid #B0B0B0;
    box-shadow: 0 3px 3px rgba(0,0,0,.5);
    -moz-box-shadow: 0 3px 3px -moz-rgba(0,0,0,.5);
    -webkit-box-shadow: 0 3px 3px rgba(0,0,0,.5);
    filter: progid:DXImageTransform.Microsoft.Shadow(color='#999999', Direction=180, Strength=3);
    z-index: 2;
    top: 100%;
	width: auto;
	display: none;
}

.MenuDropPanel a,
.MenuDropPanel a:link,
.MenuDropPanel a:visited,
.MenuDropPanel a:hover,
.MenuDropPanel a:link:hover,
.MenuDropPanel a:visited:hover
{
	color: #333333;
	text-decoration: none;
}

.MenuDropItemGroup
{
    padding: 4px 0;
}

.MenuDropItemGroupRule
{
    border-top: 1px solid #B0B0B0;
}

.MenuDropItem
{
    padding: 0 9px;
    line-height: 18px;
    font-weight: bold;
    white-space: nowrap;
    cursor: pointer;
}

.MenuDropSingle .MenuDropItemSelected
{
    display: none;
}

.MenuDropMulti .MenuDropItem
{
    padding: 0 22px;
}

.MenuDropMulti .MenuDropItemSelected
{
    background: #ECECEC url(/web/20110131204055im_/http://wwwimages.adobe.com/homepage/include/style/default/Menu/Drop/dot.png) no-repeat 7px center;
}

.MenuDropItem:hover
{
    background-color: #BDBDBD;
}

.MenuClose
{
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: 1;
}

.MenuNoJS
{
	position: relative;
	top: 8px;
	right: 33%;
	float: right;
}
