.FlashAltCanvas {
    background-color: #EEEEEE;
    display: block;
    position: relative;
    overflow: hidden;
    z-index: inherit;
    color: inherit;
}

.FlashAltCustom {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 0;
    filter:alpha(opacity=33);
    -moz-opacity:0.33;
    -khtml-opacity: 0.33;
    opacity: 0.33;
}

.FlashAltRequires {
    float: left;
    width: 210px;
    margin-right: 10px;
}

.FlashAltButton {
    float: left;
    width: 160px;
}

.FlashAltLayoutBody {
    display: block;
    padding: 10px;
    _height: 1em;
    *zoom: 100%;
}

.FlashAltLayoutBody:after {
    clear: both;
    content: "\0020";
    height: 0;
    display: block;
}

.FlashAltLayoutBlock {
    position: relative;
    padding: 10px;
    z-index: inherit;
}

.FlashAltLayoutOverlay {
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: 1;
}

