	padding: .2em .6em .3em .6em;
	line-height: 1.4em;
	width: auto;
	overflow: visible;
}

.ui-dialog .ui-resizable-se
{
	width: 14px;
	height: 14px;
	right: 3px;
	bottom: 3px;
