.Text {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: #333333;
    text-rendering: optimizeLegibility;
}

.TextEm {
    font-style: italic;
}

.TextNormal { font-weight: normal; }

.TextStrong,
.TextH5,
.TextH4,
.TextH3,
.TextH2 {
    font-weight: 600;
    font-style: normal;
}

.TextH5,
.TextSmall {
    font-size: 12px;
    line-height: 16px;
    /* Don't go below this line-height with Myriad Pro.
    You will starting getting a different baseline with bold text on floated elements. */
}

.TextH4,
.TextMedium {
    font-size: 14px;
    line-height: 19px; /* TypeKit x-height adjusted */
}

.TextH3,
.TextLarge {
    font-size: 18px;
    line-height: 21px;
}

.TextH2,
.TextLarger {
    font-size: 24px;
    line-height: 30px;
}

.TextH1,
.TextLargest {
    font-size: 30px;
    line-height: 33px;
}

.TextH1,
.TextLight,
.TextIntroHeader,
.TextIntro
{
    font-weight: 300;
}

.TextTag {
    font-size: 12px;
    line-height: 18px;
}

.TextInvert {
    color: #FFFFFF;
}

.TextInset {
    text-shadow:  0 -1px 0 #151515;
    -moz-text-shadow: 0 -1px 0 #151515;
}

.TextOutset {
    text-shadow:  0 1px 0 #151515;
    -moz-text-shadow: 0 1px 0 #151515;
}

.TextWeak {
    color: #999999;
}

.TextInvertWeak {
    color: #DDDDDD;
}

.TextLink
{
	color: #447099;
}

.TextName {
    font-weight: 600;
}

.TextAside {
    font-size: 12px;
    line-height: 15px;
    color: #666666;
    display: list-item;
    list-style-position: outside;
    list-style-type: circle;
}

.TextCenter {
    text-align: center;
}

.TextTop {
    vertical-align: top;
}

.TextBottom {
    vertical-align: bottom;
}

.TextPostScript
{
	font-style: italic;
}

.TextIntroHeader
{
	font-size: 26px;
	line-height: 32px;
}

.TextIntro
{
	font-size: 21px;
	line-height: 24px;
}
.Vis0,
.VisNone {
    visibility: hidden;
}

.Vis3 {
    filter:alpha(opacity=33);
    -moz-opacity:0.33;
    -khtml-opacity: 0.33;
    opacity: 0.33;
}

.Vis5,
.VisHalf {
    filter:alpha(opacity=50);
    -moz-opacity:0.5;
    -khtml-opacity: 0.5;
    opacity: 0.5;
}

.Vis6 {
    filter:alpha(opacity=66);
    -moz-opacity:0.66;
    -khtml-opacity: 0.66;
    opacity: 0.66;
}

.Vis7 {
    filter:alpha(opacity=70);
    -moz-opacity:0.7;
    -khtml-opacity: 0.7;
    opacity: 0.7;
}

.Vis8 {
    filter:alpha(opacity=80);
    -moz-opacity:0.8;
    -khtml-opacity: 0.8;
    opacity: 0.8;
}
.Vis85 {
    filter:alpha(opacity=85);
    -moz-opacity:0.85;
    -khtml-opacity: 0.85;
    opacity: 0.85;
}
.Vis10,
.VisFull {
    visibility: visible;
}
