/* Button
----------------------------------*/

.ui-button
{
	display: inline-block !important;
	position: relative;
	padding: 0;
	margin-right: .1em;
	text-decoration: none !important;
	cursor: pointer;
	text-align: center;
	zoom: 1;
	overflow: visible;
}

/* the overflow property removes extra width in IE */
.ui-button-icon-only
{
	width: 2.2em;
}

/* to make room for the icon, a width needs to be set here */
