/* Default: Body.CSS for default elements */

html,
body
{
    height: 100%;
}

body
{
    background-color: transparent;
}

b,
strong
{
    font-weight: bold;
}

em
{
	font-style: italic;
}
code
{
	font-family: "Courier New", Courier, monospace;
	color: #000000;
}

.Code {
    font-family: "Courier New", Courier, monospace;
    font-size: inherit;
    line-height: inherit;
}

.CodeBlock {
    display:block;
    margin: 0;
    padding: 12px 0;
    white-space:pre;
    background-color: #F5F5F5;
    _width: 100%;
    overflow: auto;
    overflow-y: visible;
    overflow-x: auto;
    font-size: 14px;
    line-height: 19px;
}

@media screen and (max-device-width: 480px) {
    .CodeBlock {
        white-space:pre-wrap;
    }
}
.FlashAltCanvas {
    background-color: #EEEEEE;
    display: block;
    position: relative;
    overflow: hidden;
    z-index: auto;
    color: inherit;
}

.FlashAltCustom {
    position: absolute;
    top: 0;
    left: 0;
    z-index: auto;
    filter:alpha(opacity=33);
    -moz-opacity:0.33;
    -khtml-opacity: 0.33;
    opacity: 0.33;
}

.FlashAltRequires {
    float: left;
    width: 210px;
    margin-right: 10px;
}

.FlashAltButton {
    float: left;
    width: 160px;
}

.FlashAltLayoutBody {
    display: block;
    padding: 10px;
    _height: 1em;
    min-height: 0;
}

.FlashAltLayoutBody:after {
    clear: both;
    content: "\0020";
    height: 0;
    display: block;
}

.FlashAltLayoutBlock {
    position: relative;
    padding: 10px;
    z-index: auto;
}

.FlashAltLayoutOverlay {
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: auto;
}

.FlashAltLayoutRow {
    display: block;
    margin-top: 5px;
    margin-bottom: 5px;
}

.FlashAltButton {
    display: block;
    background: #FFFFFF url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/FlashAlt/button.png) no-repeat left center;
    text-indent: -9999px;
    overflow: hidden;
    height: 39px;
    width: 158px;
}

.FlashAltText {
    font: normal 12px/15px "Myriad Pro", Helvetica, sans-serif;
}

.FlashAltTextLarge {
    font-size: 18px;
    line-height: 1;
}
.SearchCompositeLargeFrame,
.SearchCompositeLargeText,
.SearchCompositeLargeButton {
    height: 43px;
}

.SearchCompositeLargeFrame {
    position: relative;
    background: #FFFFFF url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/SearchForm/fieldtile.png) repeat-x;
    width: 100%;
}

.SearchCompositeLargeInput {
    height: 31px;
    width: 227px;
    margin: 6px 0 6px 6px;
    padding: 0;
    font-size: 25px;
    line-height: 1;
    border-style: none;
}

.SearchCompositeLargeRow {
    background: transparent url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/SearchForm/fieldedges.png) no-repeat 0 0;
}

.SearchCompositeLargeButton {
    position: absolute;
    top: 0;
    right: 0;
    display: block;
    width: 40px;
    margin: 0;
    padding: 0;
    text-indent: -9999px;
    border-style: none;
    background: transparent url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/SearchForm/fieldedges.png) no-repeat 0 -43px;
}

.FormInputFull
{
    width: 100%;
}
