/* Horizontal item display */

.LayoutH,
.LayoutHItems,
.LayoutHAlignRight {
    _height: 1em;
    min-height: 0;
    display: block;
}

.LayoutHItems:after,
.LayoutH:after,
.LayoutHAlignRight:after {
    content: "\0020";
    display: block;
    clear: both;
    height: 0;
}

.LayoutHItem {
    float: left;
}

.LayoutHItemRight {
    float: right;
}

/* Horizontal items alignment */

.LayoutHAlignBottom {
    vertical-align: bottom;
}

.LayoutHAlignTop {
    vertical-align: top;
}

.LayoutHAlignMiddle {
    vertical-align: middle;
}

.LayoutHAlignRight .LayoutHAlignItems {
    float: right;
}

.LayoutCenter {
    display: block;
    margin-left: auto;
    margin-right: auto;
}

.LayoutRight {
    display: block;
    float: right;
}

/* Position items absolutely within a specified container */

.LayoutP {
    position: relative;
    z-index: auto;
    display: block;
}

.LayoutPRight,
.LayoutPLeft,
.LayoutPTop,
.LayoutPBottom,
.LayoutPCenterReg,
.LayoutPCenter,
.LayoutPMiddleReg,
.LayoutPMiddle,
.LayoutPOutsideBottom,
.LayoutPOutsideTop,
.LayoutPOutsideRight,
.LayoutPOutsideLeft {
    display: block;
    position: absolute;
    visibility: visible;
    z-index: auto;
}

.LayoutPCenterReg,
.LayoutPMiddleReg {
    visibility: hidden;
}

.LayoutPCenterReg {
    left: 50%;
}

.LayoutPCenter {
    right: 50%;
    width: 100%;
}

.LayoutPMiddleReg {
    top: 50%;
}

.LayoutPMiddle {
    bottom: 50%;
    height: 100%;
}

.LayoutPOutsideBottom {
    top: 100%;
}

.LayoutPOutsideTop {
    bottom: 100%;
}

.LayoutPOutsideRight {
    left: 100%;
}

.LayoutPOutsideLeft {
    right: 100%;
}

.LayoutPLeft {
    left: 0;
}

.LayoutPRight {
    right: 0;
}

.LayoutPTop {
    top: 0;
}

.LayoutPBottom {
    bottom: 0;
}

/* Display elements */
.LayoutHidden {
    display: none;
}

.LayoutZero {
    font-size: 0;
    line-height: 0;
    padding: 0;
    margin: 0;
    border: 0;
}

.LayoutImage {
    max-width: 100%;
}

.LayoutFlip {
    -moz-transform: scale(-1);
    -o-transform: scale(-1);
    -webkit-transform: scale(-1);
    transform: scale(-1);
    filter: FlipV FlipH;
    -ms-filter: "FlipV FlipH";
}

.LayoutFlipH {
    -moz-transform: scaleX(-1);
    -o-transform: scaleX(-1);
    -webkit-transform: scaleX(-1);
    transform: scaleX(-1);
    filter: FlipH;
    -ms-filter: "FlipH";
}

.LayoutFlipV {
    -moz-transform: scaleY(-1);
    -o-transform: scaleY(-1);
    -webkit-transform: scaleY(-1);
    transform: scaleY(-1);
    filter: FlipV;
    -ms-filter: "FlipV";
