    -khtml-opacity: 0.5;
    opacity: 0.5;
}

.Vis6 {
    filter:alpha(opacity=66);
    -moz-opacity:0.66;
    -khtml-opacity: 0.66;
    opacity: 0.66;
}

.Vis7 {
    filter:alpha(opacity=70);
    -moz-opacity:0.7;
    -khtml-opacity: 0.7;
    opacity: 0.7;
}

.Vis8 {
    filter:alpha(opacity=80);
    -moz-opacity:0.8;
    -khtml-opacity: 0.8;
    opacity: 0.8;
}

.Vis10,
.VisFull {
    visibility: visible;
	filter:alpha(opacity=100);
    -moz-opacity:1;
    -khtml-opacity: 1;
