	position: relative;
	*position: none;
	_position: none;
	text-decoration: none;
	outline: none;
	vertical-align: middle;
	display: inline-block;
	cursor: default;
}

.selectBox-dropdown .selectBox-label
{
	width: 100%;
	line-height: 20px;
	padding: 0 0 0 11px;
	font-weight: bold;
	display: inline-block;
	white-space: nowrap;
	overflow: hidden;
}

.selectBox-Blue.selectBox-dropdown .selectBox-label
{
    background: url("/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Menu/Drop/filter_arrow_bg.png") no-repeat scroll right 0 transparent;
}

.selectBox-Gray.selectBox-dropdown .selectBox-label
{
    background: url("/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Menu/Drop/filter_arrow_gray_bg.png") no-repeat scroll right 0 transparent;
}

a.selectBox-Blue .selectBox-arrow,
a.selectBox-Blue:link .selectBox-arrow,
a.selectBox-Blue:hover .selectBox-arrow,
a.selectBox-Blue:visited .selectBox-arrow,
a.selectBox-Blue:visited:hover .selectBox-arrow,
a.selectBox-Gray .selectBox-arrow,
a.selectBox-Gray:link .selectBox-arrow,
a.selectBox-Gray:hover .selectBox-arrow,
a.selectBox-Gray:visited .selectBox-arrow,
a.selectBox-Gray:visited:hover .selectBox-arrow
{
	position: absolute;
	top: 0;
	right: 0;
	height: 100%;
	width: 22px;
	z-index: auto;
}

a.selectBox-Blue .selectBox-arrow,
a.selectBox-Blue:link .selectBox-arrow,
a.selectBox-Blue:hover .selectBox-arrow,
a.selectBox-Blue:visited .selectBox-arrow
{
    background: url("/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Menu/Drop/filter_arrow.png") no-repeat scroll 7px 8px transparent;
}

a.selectBox-Gray .selectBox-arrow,
a.selectBox-Gray:link .selectBox-arrow,
a.selectBox-Gray:hover .selectBox-arrow,
a.selectBox-Gray:visited .selectBox-arrow
{
    background: url("/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Menu/Drop/filter_arrow_black.png") no-repeat scroll 7px 8px transparent;
}

/* Dropdown menu */
.selectBox-dropdown-menu
{
	position: absolute;
	z-index: auto;
	max-height: 200px;
	background-color: #ECECEC;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	border-top: 0;
	border-right: 1px solid #B0B0B0;
	border-bottom: 1px solid #737373;
	border-left: 1px solid #B0B0B0;
	box-shadow: 0 3px 3px rgba(0, 0, 0, .5);
	-moz-box-shadow: 0 3px 3px -moz-rgba(0, 0, 0, .5);
	-webkit-box-shadow: 0 3px 3px rgba(0, 0, 0, .5);
	filter: progid:DXImageTransform.Microsoft.Shadow(color = '#999999', Direction = 180, Strength = 3);
	overflow: auto;
}

/* Inline control */
.selectBox-inline
{
	width: 250px;
	outline: none;
	border: solid 1px #BBB;
	background: #ECECEC;
	display: inline-block;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	border-radius: 4px;
	overflow: auto;
}

.selectBox-inline:focus
{
	border-color: #666;
}

/* Options */
.selectBox-options,
.selectBox-options LI,
.selectBox-options LI A
{
	list-style: none;
	display: block;
	cursor: default;
	padding: 0;
	margin: 0;
}

.selectBox-options LI A
{
	white-space: nowrap;
	overflow: hidden;
	line-height: 18px;
	padding: 0 11px;
	font-weight: bold;
}

.selectBox-options LI.selectBox-hover A,
.selectBox-options LI.selectBox-selected A
{
	background-color: #BDBDBD;
}

.selectBox-options LI.selectBox-disabled A
{
	color: #888;
	background-color: transparent;
}

.selectBox-options .selectBox-optgroup
{
	color: #666;
	background: #EEE;
	font-weight: bold;
	line-height: 1.5;
	padding: 0 .3em;
	white-space: nowrap;
}

/* Disabled state */
.selectBox.selectBox-disabled
{
	color: #888 !important;
}

.selectBox-dropdown.selectBox-disabled .selectBox-arrow
{
	opacity: .5;
	filter: alpha(opacity=50);
	border-color: #666;
}

.selectBox-inline.selectBox-disabled
{
	color: #888 !important;
}

.selectBox-inline.selectBox-disabled .selectBox-options A
{
	background-color: transparent !important;
}

.CPodOptionsCell .selectBox
{
    width: 163px !important;
}
