.Vis0,
.VisNone {
    visibility: hidden;
}

.Vis3 {
    filter:alpha(opacity=33);
    -moz-opacity:0.33;
    -khtml-opacity: 0.33;
    opacity: 0.33;
}

.Vis5,
.VisHalf {
    filter:alpha(opacity=50);
    -moz-opacity:0.5;
    -khtml-opacity: 0.5;
    opacity: 0.5;
}

.Vis6 {
    filter:alpha(opacity=66);
    -moz-opacity:0.66;
    -khtml-opacity: 0.66;
    opacity: 0.66;
}

.Vis7 {
    filter:alpha(opacity=70);
    -moz-opacity:0.7;
    -khtml-opacity: 0.7;
    opacity: 0.7;
}

.Vis8 {
    filter:alpha(opacity=80);
    -moz-opacity:0.8;
    -khtml-opacity: 0.8;
    opacity: 0.8;
}
.Vis85 {
    filter:alpha(opacity=85);
    -moz-opacity:0.85;
    -khtml-opacity: 0.85;
    opacity: 0.85;
}
.Vis10,
.VisFull {
    visibility: visible;
