/* Resizable
----------------------------------*/
.ui-resizable
{
	position: relative;
}

.ui-resizable-handle
{
	position: absolute;
	font-size: 0.1px;
	z-index: 99999;
	display: block;
}

.ui-resizable-disabled .ui-resizable-handle, .ui-resizable-autohide .ui-resizable-handle
{
	display: none;
}

.ui-resizable-n
{
	cursor: n-resize;
	height: 7px;
	width: 100%;
	top: -5px;
	left: 0;
}

.ui-resizable-s
{
	cursor: s-resize;
	height: 7px;
	width: 100%;
	bottom: -5px;
	left: 0;
}

.ui-resizable-e
{
	cursor: e-resize;
	width: 7px;
	right: -5px;
	top: 0;
	height: 100%;
}

.ui-resizable-w
{
	cursor: w-resize;
	width: 7px;
	left: -5px;
	top: 0;
	height: 100%;
}

.ui-resizable-se
{
	cursor: se-resize;
	width: 12px;
	height: 12px;
	right: 1px;
	bottom: 1px;
}

.ui-resizable-sw
{
	cursor: sw-resize;
	width: 9px;
	height: 9px;
	left: -5px;
	bottom: -5px;
}

.ui-resizable-nw
{
	cursor: nw-resize;
	width: 9px;
	height: 9px;
	left: -5px;
	top: -5px;
}

.ui-resizable-ne
{
	cursor: ne-resize;
	width: 9px;
	height: 9px;
	right: -5px;
	top: -5px;
}

/* Accordion
----------------------------------*/
.ui-accordion .ui-accordion-header
{
	cursor: pointer;
	position: relative;
	margin-top: 1px;
	zoom: 1;
}

.ui-accordion .ui-accordion-li-fix
{
	display: inline;
}

.ui-accordion .ui-accordion-header-active
{
	border-bottom: 0 !important;
}

.ui-accordion .ui-accordion-header
{
	display: block;
	font-size: 1em;
	padding: .5em .5em .5em .7em;
}

/* IE7-/Win - Fix extra vertical space in lists */
.ui-accordion a
{
	zoom: 1;
}

.ui-accordion-icons .ui-accordion-header a
{
	padding-left: 2.2em;
}

.ui-accordion .ui-accordion-header .ui-icon
{
	position: absolute;
	left: .5em;
	top: 50%;
	margin-top: -8px;
}

.ui-accordion .ui-accordion-content
{
	padding: 1em 2.2em;
	border-top: 0;
	margin-top: -2px;
	position: relative;
	top: 1px;
	margin-bottom: 2px;
	overflow: auto;
	display: none;
	zoom: 1;
}

.ui-accordion .ui-accordion-content-active
{
	display: block;
}

/* Autocomplete
----------------------------------*/
.ui-autocomplete
{
	position: absolute;
	cursor: default;
}

.ui-autocomplete-loading
{
	background: white url('/web/20120514232443im_/https://www.adobe.com/homepage/include/style/theme/adobe-dark/images/ui-anim_basic_16x16.gif') right center no-repeat;
}

/* workarounds */
* html .ui-autocomplete
{
	width: 1px;
}

/* without this, the menu expands to 100% in IE6 */

