.ui-tabs .ui-tabs-nav li a:active,
.ui-tabs .ui-tabs-nav li a:active:visited
{
	color: #CCCCCC;
}

.ui-tabs .ui-tabs-nav li a,
.ui-tabs.ui-tabs-collapsible
.ui-tabs-nav li.ui-tabs-selected a
{
