}

a.SiteHeaderUserItem:hover,
a.SiteHeaderUserItem:visited:hover,
.SiteHeaderUserItem a:hover,
.SiteHeaderUserItem a:visited:hover {
    text-decoration: none;
}

.SiteHeaderInfo {
