#ionCom {
    color:#333333;
    font-family: inherit;
    font-size:12px;
    line-height:15px;
}

#ionCom h3,
#ionCom h4,
#ionCom ul,
#ionCom li,
#ionCom fieldset,
#ionCom textarea,
#ionCom p,
#ionCom em {
    margin:0;
    padding:0;
}

#ionCom fieldset {
    border:none;
}

#ionCom ul {
    list-style:none;
}

#ionCom h3 em {
    font-style:normal;
}

#ionCom {
    font: inherit;
    width: auto;
}

#ionCom h3 {
    padding-bottom:10px;
    border:none;
}

#ionCom button {
    margin-top: 12px;
    color:#000;
}

#ionCom a {
    text-decoration:none;
}

#ionCom a:hover {
    text-decoration:underline;
}

#ionComHeading {
    font-weight:bold;
}

#ionComHeader {
    clear:both;
    padding:7px 0;
    vertical-align:middle;
}

#ionSummary-comments,#ionSummary-ratings {
    padding:0 7px 0 0;
}

#ionComList {
    list-style:none;
    clear:both;
}

#ionComList li {
    padding: 9px 11px;
    border-bottom: 1px solid #D9D9D9;
}

#ionComList p,#ionComPreviewTextArea {
    overflow-y:visible;
    overflow-x:auto;
}

#ionComList p {
    padding:5px 0 0;
    clear:both;
}

.ionComMetaData {
    float: left;
    clear: both;
    display:block;
}

.ionComMetaData span {
    display:block;
}

.ionComMetaData .ionComAuthor {
    float:left;
    font-size:10pt;
    color:#6c6c6c;
}

.ionComAuthor a {
    display:block;
    float:left;
    margin-right:5px;
    font-weight:bold;
}

.ionComMetaData .ionComPostDate {
    float:left;
    clear:both;
    color:#6c6c6c;
    padding:5px 0;
}

#ionComPagination {
    display:block;
    padding:10px 0 10px 12px;
    height:30px;
    line-height:30px;
    overflow:visible;
    background:#fafafa;
    border-bottom:1px solid #b2b2b2;
}

#ionComPagination li {
    display:inline;
    float:left;
}

#ionComPagination a {
    outline:none;
    padding-left:6px;
    font-weight:bold;
}

#ionComPagination strong {
    padding-left:6px;
    color:#111;
}

#ionComAddForm {
    margin:-1px 0 0;
    padding:5px;
    height:auto!important;
    min-height:20px;
    height:20px;
    overflow-y:visible;
    border-style: none;
    background-color: transparent;
}

#ionComAddForm textarea {
    width: 100%;
    padding:5px;
    margin:7px 0;
    border:1px solid #b2b2b2;
    border-style:inset;
    background:#fff;
    font-family:Arial,Helvetica,sans-serif;
    color:#000;
    font-size:12px;
}

#ionComAddForm textarea.ionComLegend {
    font-style:italic;
    color:#666;
}

#ionComAddForm fieldset a.ionButton,
.ionComAddForm fieldset a.ionButton {
    float:right;
    margin-left:5px;
}

#ionComPreview {
    margin:-14px 0 0;
    padding:12px;
    background:#eee;
    border:1px solid #047;
}

#ionComPreview h4 {
    padding-bottom:5px;
    font-size:12pt;
    font-weight:bold;
    color:#047;
}

#ionComPreview p {
    padding:5px 0;
}

.ionComMessage {
    color:#333;
    font-weight:bold;
}

.ionComNotice {
    background:#ffc;
    border-bottom:1px solid #fc0;
    color:#6c6c6c;
}

.ionComMessage a {
    color:#06c;
