}

#ionMessageContainer .ionComLoading {
    padding-left:17px;
}

.ionComLoading {
    background:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/ion/loading.gif) center left no-repeat;
}

.ionComErr,.ionComErr a {
    background:#ffeaea;
    color:#c30;
    font-weight:bold;
}

.ionComErr a:hover {
    text-decoration:none;
}

.ionLabel {
    font-weight:bold;
