    font-size:inherit;
}

#ionRatingAnchor ul {
    margin-right:5px;
}

#ionRatingAnchor span.ionLabel {
    font-size:12px;
    letter-spacing:normal;
    display:block;
    padding:0 0 5px;
}

#ionRatingAnchor .ionSummary,#ionComHeader .ionSummary {
    padding:0 0 0 2px;
}

#ionComButAdd {
    float:left;
}

#ionComRating {
    float:right;
    padding:2px 0 0;
}

#ionComRating span,
#ionComRating label {
    vertical-align:middle;
}

#ionThumbRatingGroup .ionLabel,
#ionComRating .ionLabel {
    padding-right:5px;
}

#ionComLimit {
    display:block;
    background:#ffeaea;
}

