    list-style:none;
    margin:0;
    padding:0;
}

ul.ionAnchor li {
    display:block;
    padding:5px 0 0;
}

ul.ionAnchor li.current-rating {
   padding:0;
}


.ionPositive,.ionNegative {
    font-weight:bold;
}

.ionPositive {
    color:#060;
}

.ionNegative {
    color:#900;
}

.ionClearFix {
    clear:both;
}

.inline-block {
    display:-moz-inline-block;
    display:-moz-inline-box;
    display:inline-block;
}

.inline-left {
    float:left;
}

.inline-right {
    float:right;
}

.ionIcon {
    display:block;
    padding-left:20px;
    height:16px;
}

.ionIconAdobe {
    background:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/ion/logo_adobe.gif) center left no-repeat;
}

.ionIconFeed {
    background:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/ion/rss.png) center left no-repeat;
}

.ionIconComment {
    background:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/ion/comment.png) center left no-repeat;
}

.ionStarRating,.ionStarRating a:hover,.ionStarRating a:active,.ionStarRating a:focus,.ionStarRating .current-rating {
    background:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Star/stars.png) left -1000px repeat-x;
}

.ionStarRating {
    position:relative;
    width:70px;
    height:12px;
    overflow:hidden;
    list-style:none;
    margin:0;
    padding:0;
    background-position:left top;
}

#ionComList .ionStarRating {
    float:right;
}

.ionStarRating li {
    display:inline;
}

.ionStarRating a,.ionStarRating .current-rating {
    position:absolute;
    top:0;
    left:0;
    text-indent:-1000em;
    height:12px;
    line-height:12px;
    outline:none;
    overflow:hidden;
    border:none;
}

.ionStarRating a:hover {
    background-position:left -13px;
}

.ionStarRating a.one-star {
    width:20%;
    z-index:6;
}

.ionStarRating a.two-stars {
    width:40%;
    z-index:5;
}

.ionStarRating a.three-stars {
    width:60%;
    z-index:4;
}

.ionStarRating a.four-stars {
    width:80%;
    z-index:3;
}

.ionStarRating a.five-stars {
    width:100%;
    z-index:2;
}

.ionStarRating .current-rating {
    z-index:1;
    background-position:left -13px;
}

.small-star {
    width:70px;
}

.small-star,.small-star a:hover,.small-star a:active,.small-star a:focus,.small-star .current-rating {
    background-image:url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/default/Star/stars.png);
    line-height:12px;
    height:12px;
}

.small-star .current-rating {
    background-position:left -13px;
}

#ionComAddForm a.ionButton,
.ionComAddForm a.ionButton {
    display:block;
    padding:3px 7px;
    background:#FEFEFE url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/ion/element_bg.gif) top left repeat-x;
    border-color:#C4CCCC #acb5b5 #6f7777;
    border-style:solid;
