	background: transparent url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/components/buttons/next-horizontal.png) no-repeat 0 50%;
	_right: 2px;
	_height: 300px;
}

.CarouselProducts .jcarousel-direction-rtl .jcarousel-next-horizontal
{
	left: -10px;
	right: auto;
	background-image: url(/web/20120514200925im_/http://wwwimages.adobe.com/www.adobe.com/include/style/components/buttons/prev-horizontal.png);
}

.CarouselProducts .jcarousel-next-horizontal:hover
{
	background-position: -10px 50%;
}

