	.LayoutGrid-1,
	.LayoutGrid-2,
	.NewsReelPanel,
	.NewsReelContent,
	.ThreeColumnHalved { width: 100% !important; min-width:auto!important; min-height: auto !important; margin: 0; float: none; }

	.NewsReelContent { padding: 5px 10px; width: 95% !important }
	.LayoutPLeft { width: 100%; }

	.ThreeColumnHalved { max-width: 458px !important; min-width: 286px !important; }
	.LayoutPLeft .LayoutP { position: relative; top: 0;}

	.SiteFooter { position: relative; width: 100% !important; }	
