	.SiteFooterNav { padding: 0; }	
	.SiteFooterRegionButton { left: 5px; margin-bottom: 5px;}	
	.SiteFooterNavItem { white-space: nowrap; float: none !important; display: block;}		
	#SiteFooter .LayoutHAlignItems {
		max-width: 100% !important;
		width: 100% !important;
	}	
	#sfSecurity { padding-right: 10px;}		

	.NewsReelContent { min-width: 1px !important;}	
	.LayoutGrid-2, .LayoutGrid-2-3, .LayoutGrid-2-4, .LayoutGrid-3, .LayoutGrid-3-4, .LayoutGrid-4 { margin: 0 !important; width: auto !important;}

	.CPodLayout { background: none }

	.ThreeColumn .LayoutGrid-1,
	.ThreeColumn .LayoutGrid-2,
	.ThreeColumn .LayoutGrid-1 a,
	.ThreeColumn .LayoutGrid-2 a { width: 100% !important; margin: 0; float: none; display: block;  }

	.LayoutPlayers { width: 100% !important; }

	.LayoutGrid { padding-left: 0 !important; }

	.SiteHeader, .SiteHeaderSearch { width: auto !important; }

	#shMySupport { min-width: 70px; }
	#shCompany { min-width: 64px; }

	.SiteFooter { left: 0px !important }
}

