/* Position items absolutely within a specified container */

.LayoutP {
    position: relative;
    z-index: inherit;
    display: block;
}

.LayoutPRight,
.LayoutPLeft,
.LayoutPTop,
.LayoutPBottom,
.LayoutPCenterReg,
.LayoutPCenter,
.LayoutPMiddleReg,
.LayoutPMiddle,
.LayoutPOutsideBottom,
