.PanelBevel,
.PanelBorder {
    -webkit-box-shadow: inset 0 0 0 1px rgba(0,0,0,.15);
    -moz-box-shadow: inset 0 0 0 1px rgba(0,0,0,.15);
    box-shadow: inset 0 0 0 1px rgba(0,0,0,.15);
}

.PanelBevelLeft
{
    -webkit-box-shadow: 1px 0 0 0 rgba(0, 0, 0, 0.15) inset;
    -moz-box-shadow: 1px 0 0 0 rgba(0, 0, 0, 0.15) inset;
    box-shadow: 1px 0 0 0 rgba(0, 0, 0, 0.15) inset;
}

.PanelBevelRight
{
    -webkit-box-shadow: 1px 0 1px -1px rgba(0, 0, 0, 0.55);
    -moz-box-shadow: 1px 0 1px -1px rgba(0, 0, 0, 0.55);
    box-shadow: 1px 0 1px -1px rgba(0, 0, 0, 0.55);
}

.PanelBevelTop
{
    -webkit-box-shadow: 0 1px 0 0 rgba(0, 0, 0, 0.15) inset;
    -moz-box-shadow: 0 1px 0 0 rgba(0, 0, 0, 0.15) inset;
    box-shadow: 0 1px 0 0 rgba(0, 0, 0, 0.15) inset;
}

.PanelBevelBottom
{
    -webkit-box-shadow: 0 1px 1px -1px rgba(0, 0, 0, 0.25);
    -moz-box-shadow: 0 1px 1px -1px rgba(0, 0, 0, 0.25);
    box-shadow: 0 1px 1px -1px rgba(0, 0, 0, 0.25);
}

