/* Resizable
----------------------------------*/
.ui-resizable
{
	position: relative;
}

.ui-resizable-handle
{
	position: absolute;
	font-size: 0.1px;
	z-index: 99999;
	display: block;
}

.ui-resizable-disabled .ui-resizable-handle, .ui-resizable-autohide .ui-resizable-handle
{
	display: none;
}

.ui-resizable-n
{
	cursor: n-resize;
	height: 7px;
	width: 100%;
	top: -5px;
	left: 0;
}

.ui-resizable-s
{
	cursor: s-resize;
	height: 7px;
	width: 100%;
	bottom: -5px;
	left: 0;
}

.ui-resizable-e
{
	cursor: e-resize;
	width: 7px;
	right: -5px;
	top: 0;
	height: 100%;
}

.ui-resizable-w
{
	cursor: w-resize;
	width: 7px;
	left: -5px;
	top: 0;
	height: 100%;
}

.ui-resizable-se
{
	cursor: se-resize;
	width: 12px;
	height: 12px;
	right: 1px;
	bottom: 1px;
}

.ui-resizable-sw
{
	cursor: sw-resize;
	width: 9px;
	height: 9px;
	left: -5px;
	bottom: -5px;
}

.ui-resizable-nw
{
	cursor: nw-resize;
	width: 9px;
	height: 9px;
	left: -5px;
	top: -5px;
}

.ui-resizable-ne
{
	cursor: ne-resize;
	width: 9px;
	height: 9px;
	right: -5px;
	top: -5px;
}

/* Accordion
----------------------------------*/
.ui-accordion .ui-accordion-header
{
	cursor: pointer;
	position: relative;
	margin-top: 1px;
	zoom: 1;
}

.ui-accordion .ui-accordion-li-fix
{
	display: inline;
}

.ui-accordion .ui-accordion-header-active
{
	border-bottom: 0 !important;
}

.ui-accordion .ui-accordion-header
{
	display: block;
	font-size: 1em;
	padding: .5em .5em .5em .7em;
}

/* IE7-/Win - Fix extra vertical space in lists */
.ui-accordion a
{
	zoom: 1;
}

.ui-accordion-icons .ui-accordion-header a
{
	padding-left: 2.2em;
}

.ui-accordion .ui-accordion-header .ui-icon
{
	position: absolute;
	left: .5em;
	top: 50%;
	margin-top: -8px;
}

.ui-accordion .ui-accordion-content
{
	padding: 1em 2.2em;
	border-top: 0;
	margin-top: -2px;
	position: relative;
	top: 1px;
	margin-bottom: 2px;
	overflow: auto;
	display: none;
	zoom: 1;
}

.ui-accordion .ui-accordion-content-active
{
	display: block;
}

/* Autocomplete
----------------------------------*/
.ui-autocomplete
{
	position: absolute;
	cursor: default;
}

.ui-autocomplete-loading
{
	background: white url('/web/20130101022012im_/http://wwwimages.adobe.com/www.adobe.com/include/style/theme/adobe-light/images/ui-anim_basic_16x16.gif') right center no-repeat;
}

/* workarounds */
* html .ui-autocomplete
{
	width: 1px;
}

/* without this, the menu expands to 100% in IE6 */

/* Menu
----------------------------------*/
.ui-menu
{
	list-style: none;
	padding: 2px;
	margin: 0;
	display: block;
}

.ui-menu .ui-menu
{
	margin-top: -3px;
}

.ui-menu .ui-menu-item
{
	margin: 0;
	padding: 0;
	zoom: 1;
	float: left;
	clear: left;
	width: 100%;
}

.ui-menu .ui-menu-item a
{
	text-decoration: none;
	display: block;
	padding: .2em .4em;
	line-height: 1.5;
	zoom: 1;
}

.ui-menu .ui-menu-item a.ui-state-hover,
.ui-menu .ui-menu-item a.ui-state-active
{
	font-weight: normal;
	margin: -1px;
}

/* Button
----------------------------------*/

.ui-button
{
	display: inline-block !important;
	position: relative;
	padding: 0;
	margin-right: .1em;
	text-decoration: none !important;
	cursor: pointer;
	text-align: center;
	zoom: 1;
	overflow: visible;
}

/* the overflow property removes extra width in IE */
.ui-button-icon-only
{
	width: 2.2em;
}

/* to make room for the icon, a width needs to be set here */
button.ui-button-icon-only
{
	width: 2.4em;
}

/* button elements seem to need a little more width */
.ui-button-icons-only
{
	width: 3.4em;
}

button.ui-button-icons-only
{
	width: 3.7em;
}

/*button text element */
.ui-button .ui-button-text
{
	display: block;
	line-height: 1.4;
}

.ui-button-text-only .ui-button-text
{
	padding: 0;
}

.ui-button-icon-only .ui-button-text, .ui-button-icons-only .ui-button-text
{
	padding: .4em;
	text-indent: -9999999px;
}

.ui-button-text-icon .ui-button-text, .ui-button-text-icons .ui-button-text
{
	padding: .4em 1em .4em 2.1em;
}

.ui-button-text-icons .ui-button-text
{
	padding-left: 2.1em;
	padding-right: 2.1em;
}

/* no icon support for input elements, provide padding by default */
input.ui-button
{
	padding: .4em 1em;
}

/*button icon element(s) */
.ui-button-icon-only .ui-icon, .ui-button-text-icon .ui-icon, .ui-button-text-icons .ui-icon, .ui-button-icons-only .ui-icon
{
	position: absolute;
	top: 50%;
	margin-top: -8px;
}

.ui-button-icon-only .ui-icon
{
	left: 50%;
	margin-left: -8px;
}

.ui-button-text-icon .ui-button-icon-primary, .ui-button-text-icons .ui-button-icon-primary, .ui-button-icons-only .ui-button-icon-primary
{
	left: .5em;
}

.ui-button-text-icons .ui-button-icon-secondary, .ui-button-icons-only .ui-button-icon-secondary
{
	right: .5em;
}

/*button sets*/
.ui-buttonset
{
	margin-right: 7px;
}

.ui-buttonset .ui-button
{
	margin-left: 0;
	margin-right: -.3em;
}

/* workarounds */
button.ui-button::-moz-focus-inner
{
	border: 0;
	padding: 0;
}

/* reset extra padding in Firefox */

/* Dialog
----------------------------------*/
.ui-dialog
{
	position: absolute;
	padding: 0;
	width: 300px;
	overflow: hidden;
	background: #ffffff;
	-moz-box-shadow: 0 4px 10px #333333;
	-webkit-box-shadow: 0 4px 10px #333333;
	box-shadow: 0 4px 10px #333333;
	filter: progid:DXImageTransform.Microsoft.Shadow(color = #333333, direction = 135, strength = 8) progid:DXImageTransform.Microsoft.Wheel(duration = 3);
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	border-radius: 0;
}

.ui-dialog .ui-dialog-titlebar
{
	position: relative;
	padding: 9px 20px 0 20px;
}

.ui-dialog .ui-dialog-title
{
	float: left;
	margin: 0;
}

.ui-dialog .ui-dialog-titlebar-close
{
	position: absolute;
	right: 20px;
	top: 50%;
	width: 19px;
	margin: -10px 0 0 0;
	height: 18px;
}

.ui-dialog .ui-dialog-titlebar-close span
{
	display: block;
	margin: 3px 1px 1px 1px;
}

.ui-dialog .ui-dialog-titlebar-close:hover, .ui-dialog .ui-dialog-titlebar-close:focus
{
	padding: 0;
}

.ui-dialog .ui-dialog-content
{
	border: 0;
	background: none;
	overflow: auto;
	padding: 9px 20px 20px 20px;
}

.ui-dialog .ui-dialog-buttonpane
{
	text-align: left;
	border-width: 1px 0 0 0;
	background-image: none;
	margin: .5em 0 0 0;
	padding: .3em 1em .5em .4em;
}

.ui-dialog .ui-dialog-buttonpane button
{
	float: right;
	margin: .5em .4em .5em 0;
	cursor: pointer;
	padding: .2em .6em .3em .6em;
	line-height: 1.4em;
	width: auto;
	overflow: visible;
}

.ui-dialog .ui-resizable-se
{
	width: 14px;
	height: 14px;
	right: 3px;
	bottom: 3px;
}

.ui-draggable .ui-dialog-titlebar
{
	cursor: move;
}

