#productheader a.joinnow { width: 72px; background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/joinnows.png); _background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/joinnows.gif); }
