#productheader a.howtobuy { width: 86px; background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/howtobuys.png); _background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/howtobuys.gif); }
