#productheader a.downloadnow { width: 108px; background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/downloadnows.png); _background-image: url(http://web.archive.org/web/20100701014339im_/http://images.apple.com/global/elements/buttons/downloadnows.gif); }
