#billboard .videos .video-thumb:hover .image .play { opacity:1; }
#billboard .videos .video-thumb .image img { display:block; margin:0; }

#billboard .videos .video-thumb .link { display:block; float:left; width:30%; padding-top:13px; text-decoration:none; }


/* promos */
.promos ul { clear:both; width:980px; margin:0; padding-bottom:30px; _position:relative; _z-index:1; zoom:1; }
.promos ul:after { content:'.'; display:block; height:0; clear:both; visibility:hidden; }
.promos ul li { display:inline; float:left; width:237px; height:155px; margin-left:10px;
	-webkit-box-shadow:rgba(0,0,0,.4) 0 1px 3px; -moz-box-shadow:rgba(0,0,0,.4) 0 1px 3px; box-shadow:rgba(0,0,0,.4) 0 1px 3px; box-shadow:rgba(0,0,0,.4) 0 1px 6px \0/IE9;
	-webkit-border-radius:5px; -moz-border-radius:5px; border-radius:5px;
