links:
/* generic */
@media only screen and (min-height:790px) and (min-width:980px) {
	body { overflow:hidden; }
}

#main { min-height:700px; *padding-bottom:0; *height:100%; zoom:1; }

/* wrapper view */
.gallery-content-video { display:none; width:100%; height:100%; min-height:700px; }

.gallery-wrapper { position:relative; z-index:1; overflow:hidden; min-height:700px; }
.gallery-wrapper .gallery-content-video { display:block; }

.gallery-content-video .blocker { position:absolute; z-index:3; bottom:0; left:50%; margin:-1px 0 0 -490px; width:980px; height:5px; border-bottom:1px solid #e2e2e2; }

.gallery-content-video .close { display:block; position:absolute; z-index:10; top:50%; left:50%; margin:-270px 0 0 -450px; width:28px; height:28px; text-indent:-9999px; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close.png) no-repeat 0 0; *background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close.gif); }
.gallery-content-video .close:hover { background-position:0 100%; }

.gallery-content-video .moviePanel { position:absolute; z-index:4; top:50%; left:50%; margin:-242px 0 0 -420px; width:840px; height:484px; }
.gallery-content-video .moviePanel .video { z-index:10; }

@media only screen and (max-device-width:768px) and (orientation:landscape) { /* ipad/iphone landscape */
	.gallery-wrapper,
	.gallery-content-video { min-height:672px; }
	.gallery-content-video { max-width:1024px; max-height:700px; }
}

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	.gallery-content-video .close { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close_2x.png); background-size:28px 56px; }
}

.moviePanel { width:848px !important; height:480px !important; }
.ACMediaSettingsMenu { top:0; left:0; }


/* billboard */
#billboard { position:relative; z-index:1; width:100%; padding:0; overflow:hidden; text-align:center;
	background:#fff url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/fallback_bg.png) repeat-x 0 100%;
	background:-webkit-linear-gradient(top, #fff 0%, #f5f5f5 100%);
	background:   -moz-linear-gradient(top, #fff 0%, #f5f5f5 100%);
	background:        linear-gradient(top, #fff 0%, #f5f5f5 100%);
}
#billboard a { text-decoration:none; }

#billboard .gallery-view { position:relative; z-index:1; width:800%; min-width:7872px; height:100%; max-height:1330px; min-height:700px;
	-webkit-transition:-webkit-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	   -moz-transition:   -moz-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	        transition:        transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
}
#billboard .gallery-content { position:relative; z-index:1000; float:left; width:12.5%; min-width:980px; height:100%; max-height:1330px; min-height:700px; overflow:hidden; -webkit-transform:translateZ(0); }
#billboard .gallery-content .full-span { display:block; position:absolute; z-index:3; top:0; left:0; width:100%; height:100%; overflow:hidden; text-indent:-9999px; }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .gallery-view,
	#billboard .gallery-content { min-height:672px; }
}

#billboard .resize-mask .gallery-content { opacity:0; }
#billboard .resize-mask .gallery-content.resize-active { opacity:1; }
#billboard .resize-mask .gallery-content.active { opacity:1; }

#billboard .smooth-transition .gallery-content { opacity:0; }
#billboard .smooth-transition .gallery-content.active { opacity:1; }


/* learn more and watch now */
#billboard .watch,
#billboard .learn { display:inline-block; *display:inline; margin:0 36px 0 0; width:148px; height:0; padding-top:35px; overflow:hidden; cursor:pointer; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_08c.png) no-repeat 0 0; background-size:255px 70px; zoom:1; }
#billboard .center .watch,
#billboard .center .learn { margin:6px 18px 0 18px; }
#billboard .watch:hover,
#billboard .learn:hover { background-position:0 100%; *background-position:0 205%; }
#billboard .watch { width:255px; background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_08c.png); }
#billboard .dark .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_5cc3f6.png); }
#billboard .dark .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_5cc3f6.png); }
#billboard .osx .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_78d2ff.png); }
#billboard .osx .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_78d2ff.png); }

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	#billboard .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_08c_2x.png); }
	#billboard .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_08c_2x.png); }
	#billboard .dark .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_5cc3f6_2x.png); }
	#billboard .dark .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_5cc3f6_2x.png); }
}

/* layouts within slides */
#billboard .text { display:block; position:absolute; z-index:5; top:50%; left:50%; overflow:hidden; }
#billboard .text h2,
#billboard .text h2 a { display:block; overflow:hidden; text-indent:-9999px; }
#billboard .text h2 { margin-bottom:12px; }
#billboard .image { display:block; position:absolute; z-index:1; top:50%; left:50%; }

#billboard .background { position:relative; z-index:2; width:100%; min-width:980px; height:100%; max-height:1330px; min-height:700px; overflow:hidden; }
#billboard .image { display:block; position:absolute; z-index:1; top:50%; left:50%; }
#billboard .blur { display:block; position:absolute; z-index:1; left:50%; }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .blur { left:0; top:-112px; margin:0 !important; width:1024px !important; height:160px !important; background-size:1024px 160px !important; }
}


/* scrim */
#billboard .gallery-content .scrim { display:block; position:absolute; z-index:1000; left:0; right:0; bottom:-152px; height:200px; overflow:hidden; background:#fafafa; visibility:hidden; }
#billboard .initial-resize .gallery-content .scrim { visibility:visible; }
@media only screen and (min-device-width:769px) { /* desktop */
	#billboard .gallery-content .scrim {
		-webkit-backface-visibility:hidden;
		   -moz-backface-visibility:hidden;
		        backface-visibility:hidden;
	}
}

#billboard .gallery-content .scrim .fill { position:absolute; z-index:1; top:0; left:0; bottom:0; right:0; width:100%; height:100%; background:rgba(255,255,255,.2); }
#billboard.light .gallery-content .scrim .fill { background:rgba(255,255,255,.55); }

/* nav (overlay on top of scrim) */
#billboard .nav { display:block; position:absolute; z-index:1002; left:0; bottom:0; right:0; height:48px; -webkit-tap-highlight-color:transparent; }
#billboard .nav .wrapper { margin:0 auto; width:980px; padding:1em 0 .8em; }

#billboard .nav ul { margin:0; width:100%; }
#billboard .nav li,
#billboard .nav li a { display:-moz-inline-stack; display:inline-block; *display:inline; zoom:1; }
#billboard .nav li a { -webkit-tap-highlight-color:rgba(0,0,0,.25); }

/* text items (see all/close) */
#billboard .nav .left { position:absolute; left:0; margin:0; }
@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard.hover-see-all .nav .left { top:-29px; }
}

/* dot items */
#billboard .nav .dots { position:relative; z-index:1002; height:100px; }

#billboard .nav .dot { position:relative; z-index:1; margin:0 8px; width:12px; height:24px; padding:0; line-height:0; }
#billboard .nav .dot a { position:absolute; z-index:1; top:8px; left:2px; width:8px; height:8px; background:#08c; outline:none; text-indent:-99999px; cursor:default;
	-webkit-border-radius:50%;
	   -moz-border-radius:50%;
	        border-radius:50%;
}
#billboard .nav .dot a:hover { cursor:pointer; }
#billboard .nav .dot a.active { cursor:default; top:6px; left:0; width:10px; height:10px; border:1px solid #08c; background-color:transparent; }

#billboard.dark .nav .dot a { background:#fff; }
#billboard.dark .nav .dot a.active { border-color:#fff; background-color:transparent; }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .nav .dot a { -webkit-pointer-events:none; pointer-events:none; }
}


/* thumbnails */
#billboard .nav .thumbnails { position:relative; z-index:1003; }
#billboard .nav .thumbnails .thumb a { position:relative; display:block; z-index:1; margin:1px 3px; width:134px; height:100px; border:none; overflow:hidden; text-indent:-9999px; background-repeat:no-repeat; background-position:0 0; background-size:134px 100px; }
#billboard .nav .thumbnails .thumb a span { display:block; position:absolute; z-index:2; top:0; left:0; bottom:0; right:0; border:1px solid transparent; }
#billboard .nav .thumbnails .thumb a.active { cursor:default; }
#billboard .nav .thumbnails .thumb a.active span { border-color:#5cc3f6; }

#billboard .nav .thumbnails .thumb a.ios { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/ios_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.osx { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/osx_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.mac-pro { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/macpro_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.macbook-air { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/macbookair_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.designed-by-apple { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/designed_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.itunes-radio { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/itunesradio_thumb.jpg); }
#billboard .nav .thumbnails .thumb a.wwdc { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/wwdc_thumb.jpg); }

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	#billboard .nav .thumbnails .thumb a.ios { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/ios_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.osx { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/osx_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.mac-pro { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/macpro_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.macbook-air { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/macbookair_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.designed-by-apple { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/designed_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.itunes-radio { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/itunesradio_thumb_2x.jpg); }
	#billboard .nav .thumbnails .thumb a.wwdc { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/wwdc_thumb_2x.jpg); }
}

/* see all/close carets links */
#billboard .nav .caret { padding-left:30px; height:25px; overflow:hidden; font-size:13px; line-height:24px; color:#08c; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/caret_up.png) no-repeat 0 -1px; background-size:25px 50px; }
#billboard .nav .caret.down { display:none; background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/caret_down.png); }
#billboard.dark .nav .caret { color:#fff; background-position:0 -26px; }

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	#billboard .nav .caret { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/caret_up_2x.png); }
	#billboard .nav .caret.down { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/caret_down_2x.png); }
}

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .nav .caret.down { display:block; }
}


/* show/hide scrim */
#billboard .nav .dots { opacity:1;
	-webkit-transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	   -moz-transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	        transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
}
#billboard .nav:hover .dots { opacity:0;
	-webkit-transform:translate3d(0, -40px, 0);
	   -moz-transform:translate3d(0, -40px, 0);
	        transform:translate3d(0, -40px, 0);
	-webkit-transition:all 100ms ease-out;
	   -moz-transition:all 100ms ease-out;
	        transition:all 100ms ease-out;

}
#billboard.hover-see-all .nav .caret { *display:none; }
#billboard.hover-see-all .nav .dots { opacity:0\9; }

#billboard .nav .thumbnails { opacity:0;
	-webkit-transform:translate3d(0, 0, 0);
	   -moz-transform:translate3d(0, 0, 0);
	        transform:translate3d(0, 0, 0);
	-webkit-transition:-webkit-transform 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860), opacity 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860);
	   -moz-transition:   -moz-transform 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860), opacity 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860);
	        transition:        transform 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860), opacity 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860);
	-webkit-transition:translateZ(0);
}
#billboard .nav:hover .thumbnails { opacity:1;
	-webkit-transform:translate3d(0, -171px, 0);
	   -moz-transform:translate3d(0, -171px, 0);
	        transform:translate3d(0, -171px, 0);
	-webkit-transition:-webkit-transform 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975), opacity 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	   -moz-transition:   -moz-transform 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975), opacity 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	        transition:        transform 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975), opacity 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
}
#billboard.hover-see-all .nav .thumbnails { top:-171px\9; }

#billboard .scrim {
	-webkit-transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	   -moz-transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	        transition:all 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
}
@media only screen and (min-device-width:769px) { /* desktop */
	#billboard .scrim {
		-webkit-backface-visibility:hidden;
		   -moz-backface-visibility:hidden;
		        backface-visibility:hidden;
	}
}

#billboard .scrim .blur {
	-webkit-transition:margin 0.01s;
	   -moz-transition:margin 0.01s;
	        transition:margin 0.01s;
}
@media only screen and (min-device-width:769px) { /* desktop */
	#billboard .scrim .blur {
		-webkit-backface-visibility:hidden;
		   -moz-backface-visibility:hidden;
		        backface-visibility:hidden;
	}
}

@media only screen and (min-device-width:769px) { /* desktop */
	#billboard .resize-mask .photo .scrim,
	#billboard .resize-mask .photo .scrim .blur {
		-webkit-backface-visibility:visible;
		   -moz-backface-visibility:visible;
		        backface-visibility:visible;
	}
}

#billboard.open-see-all .blur {
	-webkit-transition:-webkit-transform 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	   -moz-transition:   -moz-transform 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
	        transition:        transform 200ms cubic-bezier(0.600, 0.040, 0.940, 0.665);
}

#billboard.hover-see-all .scrim {
	-webkit-transform:translate3d(0, -64px, 0);
	   -moz-transform:translate3d(0, -64px, 0);
	        transform:translate3d(0, -64px, 0);
	-webkit-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	   -moz-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	        transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
}
#billboard.hover-see-all .scrim .blur {
	-webkit-transform:translate3d(0, 64px, 0);
	   -moz-transform:translate3d(0, 64px, 0);
	        transform:translate3d(0, 64px, 0);
}
#billboard.hover-see-all .active .scrim .blur {
	-webkit-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	   -moz-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	        transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
}

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .scrim { -webkit-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975); }
	#billboard .scrim .blur { -webkit-transition:all 450ms cubic-bezier(0.070, 0.095, 0.000, 0.975); }

	#billboard .nav:hover .dots { opacity:1; -webkit-transform:translate3d(0, 0, 0); }
	#billboard .nav:hover .thumbnails { -webkit-transform:translate3d(0, 0, 0); }

	#billboard.hover-see-all .nav .dots,
	#billboard.hover-see-all .nav:hover .dots { opacity:0;
		-webkit-transform:translate3d(0, -69px, 0);
		-webkit-transition:-webkit-transform 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860), opacity 400ms cubic-bezier(0.785, 0.135, 0.150, 0.860);
	}
	#billboard.hover-see-all .nav .thumbnails,
	#billboard.hover-see-all .nav:hover .thumbnails { opacity:1;
		-webkit-transform:translate3d(0, -190px, 0) scale(1,1);
		-webkit-transition:-webkit-transform 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975), opacity 400ms cubic-bezier(0.070, 0.095, 0.000, 0.975);
	}
	#billboard.hover-see-all .scrim { -webkit-transform:translate3d(0, -112px, 0); }
	#billboard.hover-see-all .scrim .blur { -webkit-transform:translate3d(0, 112px, 0); }
}
@media only screen and (max-device-width:768px) and (orientation:landscape) {
	#billboard .scrim .blur {
		-webkit-transform:translate3d(0, 6px, 0);
	}
	#billboard.hover-see-all .scrim .blur {
		-webkit-transform:translate3d(0, 112px, 0);
	}
}

/* paddle nav */
#billboard .paddle-nav { width:100%; height:100%; *margin-top:-100px; *height:100px; }
#billboard .paddle-nav li { _display:none; zoom:1; }
#billboard .paddle-nav li .arrow { display:block; opacity:0; position:absolute; z-index:1002; top:108px; bottom:108px; padding:0 25px; outline:none; cursor:pointer; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/global/elements/blank.gif); zoom:1; -webkit-transform:translateZ(0);
	-webkit-transition:opacity 0.25s ease-out;
	   -moz-transition:opacity 0.25s ease-out;
	        transition:opacity 0.25s ease-out;
}
#billboard .paddle-nav li .arrow:hover { opacity:1; }
#billboard .paddle-nav li .arrow.disabled { cursor:default; opacity:0; filter:alpha(opacity=0); }
#billboard .paddle-nav li .arrow b { position:absolute; z-index:1002; display:block; top:50%; margin-top:-32px; width:36px; height:71px; padding:20px 14px; text-indent:-9999px; overflow:hidden; background-repeat:no-repeat; background-position:50% 50%; background-size:36px 71px; behavior:url(/web/20130614200000im_/http://images.apple.com/global/scripts/lib/ie7pngfix.htc); zoom:1; -webkit-transform:translateZ(0); }
#billboard .paddle-nav li .arrow.prev { left:0; right:50%; margin-right:475px; }
#billboard .paddle-nav li .arrow.prev b { left:38px; background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/paddle_prev_999.png); }
#billboard .paddle-nav li .arrow.next { right:0; left:50%; margin-left:475px; }
#billboard .paddle-nav li .arrow.next b { right:38px; background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/paddle_next_999.png); }
@media screen and (max-width:1150px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev b { left:0; }
	#billboard .paddle-nav li .arrow.next b { right:0; }
}
@media screen and (max-width:1075px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev { margin-right:440px; }
	#billboard .paddle-nav li .arrow.next { margin-left:440px; }
}
@media screen and (max-width:1030px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev { margin-right:433px; }
