css:
/* generic */
@media only screen and (min-height:790px) and (min-width:980px) {
	body { overflow:hidden; }
}

#main { min-height:700px; *padding-bottom:0; *height:100%; zoom:1; }

/* wrapper view */
.gallery-content-video { display:none; width:100%; height:100%; min-height:700px; }

.gallery-wrapper { position:relative; z-index:1; overflow:hidden; min-height:700px; }
.gallery-wrapper .gallery-content-video { display:block; }

.gallery-content-video .blocker { position:absolute; z-index:3; bottom:0; left:50%; margin:-1px 0 0 -490px; width:980px; height:5px; border-bottom:1px solid #e2e2e2; }

.gallery-content-video .close { display:block; position:absolute; z-index:10; top:50%; left:50%; margin:-270px 0 0 -450px; width:28px; height:28px; text-indent:-9999px; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close.png) no-repeat 0 0; *background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close.gif); }
.gallery-content-video .close:hover { background-position:0 100%; }

.gallery-content-video .moviePanel { position:absolute; z-index:4; top:50%; left:50%; margin:-242px 0 0 -420px; width:840px; height:484px; }
.gallery-content-video .moviePanel .video { z-index:10; }

@media only screen and (max-device-width:768px) and (orientation:landscape) { /* ipad/iphone landscape */
	.gallery-wrapper,
	.gallery-content-video { min-height:672px; }
	.gallery-content-video { max-width:1024px; max-height:700px; }
}

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	.gallery-content-video .close { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/close_2x.png); background-size:28px 56px; }
}

.moviePanel { width:848px !important; height:480px !important; }
.ACMediaSettingsMenu { top:0; left:0; }


/* billboard */
#billboard { position:relative; z-index:1; width:100%; padding:0; overflow:hidden; text-align:center;
	background:#fff url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/v/home/u/images/fallback_bg.png) repeat-x 0 100%;
	background:-webkit-linear-gradient(top, #fff 0%, #f5f5f5 100%);
	background:   -moz-linear-gradient(top, #fff 0%, #f5f5f5 100%);
	background:        linear-gradient(top, #fff 0%, #f5f5f5 100%);
}
#billboard a { text-decoration:none; }

#billboard .gallery-view { position:relative; z-index:1; width:800%; min-width:7872px; height:100%; max-height:1330px; min-height:700px;
	-webkit-transition:-webkit-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	   -moz-transition:   -moz-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	        transition:        transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
}
#billboard .gallery-content { position:relative; z-index:1000; float:left; width:12.5%; min-width:980px; height:100%; max-height:1330px; min-height:700px; overflow:hidden; -webkit-transform:translateZ(0); }
#billboard .gallery-content .full-span { display:block; position:absolute; z-index:3; top:0; left:0; width:100%; height:100%; overflow:hidden; text-indent:-9999px; }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .gallery-view,
	#billboard .gallery-content { min-height:672px; }
}

#billboard .resize-mask .gallery-content { opacity:0; }
#billboard .resize-mask .gallery-content.resize-active { opacity:1; }
#billboard .resize-mask .gallery-content.active { opacity:1; }

#billboard .smooth-transition .gallery-content { opacity:0; }
#billboard .smooth-transition .gallery-content.active { opacity:1; }


/* learn more and watch now */
#billboard .watch,
#billboard .learn { display:inline-block; *display:inline; margin:0 36px 0 0; width:148px; height:0; padding-top:35px; overflow:hidden; cursor:pointer; background:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_08c.png) no-repeat 0 0; background-size:255px 70px; zoom:1; }
#billboard .center .watch,
#billboard .center .learn { margin:6px 18px 0 18px; }
#billboard .watch:hover,
#billboard .learn:hover { background-position:0 100%; *background-position:0 205%; }
#billboard .watch { width:255px; background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_08c.png); }
#billboard .dark .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_5cc3f6.png); }
#billboard .dark .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_5cc3f6.png); }
#billboard .osx .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_78d2ff.png); }
#billboard .osx .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_78d2ff.png); }

@media only screen and (-webkit-min-device-pixel-ratio:1.5), only screen and (min-device-pixel-ratio:1.5) { /* retina */
	#billboard .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_08c_2x.png); }
	#billboard .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_08c_2x.png); }
	#billboard .dark .watch { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/watch_keynote_5cc3f6_2x.png); }
	#billboard .dark .learn { background-image:url(http://web.archive.org/web/20130614200000im_/http://images.apple.com/home/images/learn_more_5cc3f6_2x.png); }
}

/* layouts within slides */
#billboard .text { display:block; position:absolute; z-index:5; top:50%; left:50%; overflow:hidden; }
#billboard .text h2,
#billboard .text h2 a { display:block; overflow:hidden; text-indent:-9999px; }
#billboard .text h2 { margin-bottom:12px; }
#billboard .image { display:block; position:absolute; z-index:1; top:50%; left:50%; }

#billboard .background { position:relative; z-index:2; width:100%; min-width:980px; height:100%; max-height:1330px; min-height:700px; overflow:hidden; }
#billboard .image { display:block; position:absolute; z-index:1; top:50%; left:50%; }
