@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .blur { left:0; top:-112px; margin:0 !important; width:1024px !important; height:160px !important; background-size:1024px 160px !important; }
}


/* scrim */
#billboard .gallery-content .scrim { display:block; position:absolute; z-index:1000; left:0; right:0; bottom:-152px; height:200px; overflow:hidden; background:#fafafa; visibility:hidden; }
#billboard .initial-resize .gallery-content .scrim { visibility:visible; }
@media only screen and (min-device-width:769px) { /* desktop */
	#billboard .gallery-content .scrim {
		-webkit-backface-visibility:hidden;
		   -moz-backface-visibility:hidden;
		        backface-visibility:hidden;
	}
}

#billboard .gallery-content .scrim .fill { position:absolute; z-index:1; top:0; left:0; bottom:0; right:0; width:100%; height:100%; background:rgba(255,255,255,.2); }
#billboard.light .gallery-content .scrim .fill { background:rgba(255,255,255,.55); }

/* nav (overlay on top of scrim) */
#billboard .nav { display:block; position:absolute; z-index:1002; left:0; bottom:0; right:0; height:48px; -webkit-tap-highlight-color:transparent; }
#billboard .nav .wrapper { margin:0 auto; width:980px; padding:1em 0 .8em; }

#billboard .nav ul { margin:0; width:100%; }
#billboard .nav li,
#billboard .nav li a { display:-moz-inline-stack; display:inline-block; *display:inline; zoom:1; }
#billboard .nav li a { -webkit-tap-highlight-color:rgba(0,0,0,.25); }

/* text items (see all/close) */
#billboard .nav .left { position:absolute; left:0; margin:0; }
@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard.hover-see-all .nav .left { top:-29px; }
}

/* dot items */
#billboard .nav .dots { position:relative; z-index:1002; height:100px; }

#billboard .nav .dot { position:relative; z-index:1; margin:0 8px; width:12px; height:24px; padding:0; line-height:0; }
#billboard .nav .dot a { position:absolute; z-index:1; top:8px; left:2px; width:8px; height:8px; background:#08c; outline:none; text-indent:-99999px; cursor:default;
	-webkit-border-radius:50%;
	   -moz-border-radius:50%;
	        border-radius:50%;
}
#billboard .nav .dot a:hover { cursor:pointer; }
#billboard .nav .dot a.active { cursor:default; top:6px; left:0; width:10px; height:10px; border:1px solid #08c; background-color:transparent; }

#billboard.dark .nav .dot a { background:#fff; }
#billboard.dark .nav .dot a.active { border-color:#fff; background-color:transparent; }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .nav .dot a { -webkit-pointer-events:none; pointer-events:none; }
}


