/* Fast Backward/Forward Display */
.ACMediaControls .ACMediaAlertDisplay { display: none; }
.ACMediaControls .ACMediaAlertDisplay-active { display: block; left: 20px; position: absolute !important; top: 20px; z-index: 20050; }

.ACMediaControls .ACMediaAlertDisplay-active div.two-times-speed-display,
.ACMediaControls .ACMediaAlertDisplay-active div.four-times-speed-display,
.ACMediaControls .ACMediaAlertDisplay-active div.eight-times-speed-display { height: 40px; text-indent: -99999px; width: 40px; *color: #FFF; *font-size: 15px; *font-weight: bold; *text-indent: 0; _background: none; _color: #FFF; _font-size: 15px; _font-weight: bold; _text-indent: 0; _text-shadow: #000 1px 1px 2px; top: 0; }
.ACMediaControls .ACMediaAlertDisplay-active div.two-times-speed-display { background-position: -1px -170px; height: 40px; width: 39px; *background: none; }
.ACMediaControls .ACMediaAlertDisplay-active div.four-times-speed-display { background-position: -40px -170px; *background: none; }
.ACMediaControls .ACMediaAlertDisplay-active div.eight-times-speed-display { background-position: -80px -170px; *background: none; }

.ACMediaControls .ACMediaAlertDisplay-active div .border-radius-box { margin: 0; padding: 0; position: relative; }
.ACMediaControls .ACMediaAlertDisplay-active div .border-radius-box span { left: 7px; position: absolute !important; top: 10px; }

.slim .ACMediaControls .fastBackward-speed-display,
.slim .ACMediaControls .fastForward-speed-display { display: none; }

/* Text Display */
.ACMediaControls .ACMediaAlertDisplay-active .ACMediaCaptionsDisplay { background: rgba(0,0,0,0.5); border-radius: 7px; color: #FFF; font-size: 15px; font-weight: bold; padding: 7px 10px; text-shadow: #000 1px 1px 2px; -webkit-border-radius: 7px; -moz-border-radius: 7px; -o-border-radius: 7px; _height: 40px; _margin: 0; _padding: 0; _width: 210px; }
.ACMediaControls .ACMediaAlertDisplay-active .ACMediaCaptionsDisplay .border-radius-box { height: 40px; margin: 0; padding: 0; position: relative; width: 210px; }
.ACMediaControls .ACMediaAlertDisplay-active .ACMediaCaptionsDisplay .border-radius-box span { left: 10px; position: absolute !important; top: 10px; white-space: nowrap; }

/* Track Text */
.ACMediaControls .ACMediaTrackText-enabled { bottom: 30px; height: auto; left: 0; position: absolute !important; right: 0; margin: 0 auto; width: 70%; }
.ACMediaControls .ACMediaTrackText-enabled span { background: #000; clear: left; color: #FFF; float: left; font-family: Monaco; font-size: 14px; height: 20px; letter-spacing: 4px; padding: 2px 12px; white-space: nowrap; }

