/* iphone 5s gradient backgrounds */
.gold-texture, .gold-texture-reverse { background:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/texture_gold.png); }
.gold-texture { background:url(/web/20131101062700im_/http://images.apple.com/v/home/ae/images/texture_50.png), url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/gradient_gold.png); background-size:auto,20px 100%; }
.gold-texture-reverse { background:url(/web/20131101062700im_/http://images.apple.com/v/home/ae/images/texture_50.png), url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/gradient_gold_reverse.png); background-size:auto,20px 100%; }

#homefooter { background:#f2f2f2; }
#billboard { position:relative; z-index:1; width:100%; padding:0; overflow:hidden; text-align:center; }
#billboard a { text-decoration:none; }

html,
body,
#main,
#gallery-wrapper,
#gallery-content-wrapper-billboard,
#billboard.gallery { height:100%; }

#billboard .gallery-view { position:relative; z-index:1; width:400%; height:100%;
	-webkit-transition:-webkit-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	   -moz-transition:   -moz-transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
	        transition:        transform 0.8s cubic-bezier(0, 0, 0.25, 1) .2s;
}

#billboard .gallery-view.pre-resize { height:100%; visibility:hidden; }
#billboard .gallery-view.initial-resize { height:100%; visibility:visible; }

#billboard .gallery-content { position:relative; z-index:1000; float:left; min-width:1115px; width:25%; height:100%; overflow:hidden; -webkit-transform:translateZ(0); }
#billboard .gallery-content.clone { z-index:10; }

#billboard .gallery-content .inner { padding-top:53px; position:relative; height:100%; min-height:100%; -moz-box-sizing:border-box; -webkit-box-sizing:border-box; box-sizing:border-box; }


/* flexbox
===================================== */
@media only screen and (min-height:800px) and (min-device-width:1025px) {
	#billboard.flexible .gallery-content .flex-container { height:100%;
	display:-webkit-box;
	display:   -moz-box;
	display:-webkit-flex;
	display: -ms-flexbox;
	display:        flex;
	-webkit-box-orient:vertical;
	   -moz-box-orient:vertical;
	-webkit-flex-direction:column;
		-ms-flex-direction:column;
			flex-direction:column;
	}

	#billboard.flexible .gallery-content .flex-container .top {
	display:-webkit-box;
	display:   -moz-box;
	display:-webkit-flex;
	display: -ms-flexbox;
	display:        flex;
	  -webkit-box-align:center;
	     -moz-box-align:center;
	-webkit-align-items:center;
		 -ms-flex-align:center;
			align-items:center;
	-webkit-box-flex:1 1;
	       -moz-box-flex:1 1;
		-webkit-flex:1 1;
			-ms-flex:1 1;
				flex:1 1;
	}

	#billboard.flexible .gallery-content .flex-container .bottom {
	display:-webkit-box;
	display:   -moz-box;
	display:-webkit-flex;
	display: -ms-flexbox;
	display:        flex;
	  -webkit-box-align:center;
	     -moz-box-align:center;
	-webkit-align-items:center;
		 -ms-flex-align:center;
			align-items:center;
	}
}


/* Min/Max Heights
===================================== */

#gallery-wrapper,
#gallery-content-wrapper-billboard,
#billboard,
#billboard .gallery-view,
#billboard .gallery-content,
#billboard .background {
	min-height:672px;
	max-height:1440px;
	*max-height:1000px;
	*min-width:980px;
}

/* huge screen */
@media only screen and (min-height:1440px) {
	#homefooter { min-height:119px; }
}

/* ios */
@media only screen and (max-device-width:768px) {
	#homefooter { min-height:0; }
	#gallery-wrapper,
	#gallery-content-wrapper-billboard,
	#billboard { height:auto; }
	#billboard,
	#billboard .gallery-view,
	#billboard .gallery-content,
	#billboard .background { min-height:0; max-height:none; }
}

/* iphone portrait */
@media only screen and (min-device-width:320px) and (max-device-width:480px) {
	#gallery-wrapper,
	#gallery-content-wrapper-billboard,
	#billboard,
	#billboard .gallery-view,
	#billboard .gallery-content,
	#billboard .background { min-height:1140px; max-height:none; }
}

/* iphone landscape short */
@media only screen and (min-device-width:320px) and (max-device-width:480px) and (orientation:landscape) and (max-height:589px),
       only screen and (min-device-width:320px) and (max-device-width:480px) and (orientation:landscape) and (max-height:549px) and (device-aspect-ratio:40/71) {
	#gallery-wrapper,
	#gallery-content-wrapper-billboard,
	#billboard,
	#billboard .gallery-view,
	#billboard .gallery-content,
	#billboard .background { min-height:550px; }
}

/* iphone landscape fullscreen */
@media only screen and (min-device-width:320px) and (max-device-width:480px) and (orientation:landscape) and (min-height:590px),
       only screen and (min-device-width:320px) and (max-device-width:480px) and (orientation:landscape) and (min-height:550px) and (device-aspect-ratio:40/71) {
	#gallery-wrapper,
	#gallery-content-wrapper-billboard,
	#billboard,
	#billboard .gallery-view,
	#billboard .gallery-content,
	#billboard .background { min-height:550px; *min-height:1000px; }
}

/* layouts within slides
=====================================*/

/* nav
==================================== */
#billboard .nav { display:block; position:absolute; z-index:1002; left:0; bottom:-4px; right:0; height:48px; -webkit-tap-highlight-color:transparent; }
#billboard .nav .wrapper { margin:0 auto; width:980px; padding:1em 0 .8em; }

@media only screen and (min-device-width:320px) and (max-device-width:480px),
       only screen and (min-device-width:481px) and (max-device-width:768px) {
	#billboard .nav { top:auto; bottom:0; *bottom:-4px; }
}

/* ipad */
@media only screen and (min-device-width:481px) and (max-device-width:768px) {
	#billboard .nav { bottom:16px; }
}

#billboard .nav ul { margin:0; width:100%; }
#billboard .nav .dot,
#billboard .nav .dot a { display:-moz-inline-stack; display:inline-block; *display:inline; zoom:1; }
#billboard .nav .dot a { -webkit-tap-highlight-color:rgba(0,0,0,.25); }


/* dot nav */
.dots .dot { position:relative; margin:0 8px; width:12px; height:12px; }
.dots .dot a { position:absolute; top:2px; left:2px; width:8px; height:8px; text-indent:100%; white-space:nowrap; overflow:hidden; background:#949494; outline:none; -webkit-transform:translateZ(0);
	-webkit-box-shadow:none;
       -moz-box-shadow:none;
			box-shadow:none;
	-webkit-border-radius:50%;
       -moz-border-radius:50%;
			border-radius:50%;
	-webkit-transition:background-color 0.25s, border-color 0.25s;
       -moz-transition:background-color 0.25s, border-color 0.25s;
	        transition:background-color 0.25s, border-color 0.25s;

}
.dots .dot a:hover { background:#666; }
.dots .dot a.active { top:0px; left:0; border:1px solid #08c; background:transparent; width:10px; height:10px; border:1px solid transparent;
	-webkit-box-shadow:none;
       -moz-box-shadow:none;
			box-shadow:none;
}

@media only screen and (min-device-width:320px) and (max-device-width:480px) {
	#billboard .nav { height:40px; }
	.dots .dot { margin:0 8px; width:20px; height:20px; }
	.dots .dot a { top:2px; left:2px; height:10px; width:10px; border:2px solid transparent; }
	.dots .dot a.active { height:16px; width:16px; }
}

#billboard.black .dots .dot a { background:#000; }
#billboard.black .dots .dot a.active { border-color:#000; background-color:transparent; }

#billboard.gray .dots .dot a { background:#808080; }
#billboard.gray .dots .dot a.active { border-color:#808080; background-color:transparent; }

#billboard.dark .dots .dot a { background:#fff; }
#billboard.dark .dots .dot a.active { border-color:#fff; background-color:transparent; }

/* paddle nav */
#billboard .paddle-nav { width:100%; height:100%; *margin-top:-100px; *height:100px; }
#billboard .paddle-nav li { _display:none; zoom:1; }
#billboard .paddle-nav li .arrow { display:block; opacity:0; filter:alpha(opacity=0); position:absolute; z-index:1002; top:108px; bottom:108px; padding:0 25px; outline:none; cursor:pointer; background:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/global/elements/blank.gif); zoom:1; -webkit-transform:translateZ(0);
	-webkit-transition:opacity 0.25s ease-out;
       -moz-transition:opacity 0.25s ease-out;
	        transition:opacity 0.25s ease-out;
}
#billboard .paddle-nav li .arrow:hover { opacity:1; filter:alpha(opacity=100); }
#billboard .paddle-nav li .arrow.disabled { cursor:default; opacity:0; filter:alpha(opacity=0); }
#billboard .paddle-nav li .arrow b { position:absolute; z-index:1002; display:block; top:50%; margin:-15px 14px 20px 0; width:36px; height:71px; text-indent:-9999px; overflow:hidden; background-repeat:no-repeat; background-position:50% 50%; background-size:36px 71px; behavior:url(/web/20131101062700im_/http://images.apple.com/global/scripts/lib/ie7pngfix.htc); zoom:1; -webkit-transform:translateZ(0); }
#billboard .paddle-nav li .arrow.prev { left:0; right:50%; margin-right:475px; *right:auto; *min-width:20px; *width:15%; *max-width:500px; }
#billboard .paddle-nav li .arrow.prev b { left:38px; background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_999.png); }
#billboard .paddle-nav li .arrow.next { right:0; left:50%; margin-left:475px; *left:auto; *width:20px; *width:15%; *max-width:500px; }
#billboard .paddle-nav li .arrow.next b { right:38px; background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_999.png); }
@media screen and (max-width:1150px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev b { left:0; }
	#billboard .paddle-nav li .arrow.next b { right:0; }
}
@media screen and (max-width:1075px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev { margin-right:440px; }
	#billboard .paddle-nav li .arrow.next { margin-left:440px; }
}
@media screen and (max-width:1030px) { /* responsive */
	#billboard .paddle-nav li .arrow.prev { margin-right:433px; }
	#billboard .paddle-nav li .arrow.next { margin-left:433px; }
}

#billboard.midtone .paddle-nav li .arrow.next b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_ddd.png); }
#billboard.midtone .paddle-nav li .arrow.prev b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_ddd.png); }

#billboard.dark .paddle-nav li .arrow.next b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_fff.png); }
#billboard.dark .paddle-nav li .arrow.prev b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_fff.png); }

@media only screen and (-webkit-min-device-pixel-ratio:1.5),
       only screen and (min-device-pixel-ratio:1.5) { /* retina */
	#billboard .paddle-nav li .arrow.next b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_999_2x.png); }
	#billboard .paddle-nav li .arrow.prev b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_999_2x.png); }

	#billboard.midtone .paddle-nav li .arrow.next b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_ddd_2x.png); }
	#billboard.midtone .paddle-nav li .arrow.prev b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_ddd_2x.png); }

	#billboard.dark .paddle-nav li .arrow.next b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_fff_2x.png); }
	#billboard.dark .paddle-nav li .arrow.prev b { background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_fff_2x.png); }
}

#billboard .paddle-nav li .arrow.prev b { *background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_prev_999.png); }
#billboard .paddle-nav li .arrow.next b { *background-image:url(http://web.archive.org/web/20131101062700im_/http://images.apple.com/v/home/ae/images/paddle_next_999.png); }

@media only screen and (max-device-width:768px) { /* ipad/iphone */
	#billboard .paddle-nav { display:none !important; }
}

/* slide shared
------------------------*/

/* ir */
#billboard .text .title,
#billboard .text .subhead,
#billboard .cta .watch,
#billboard .cta .video,
#billboard .cta .learn,
#billboard .cta .tv-spot { text-indent:100%; white-space:nowrap; overflow:hidden; }

#billboard .text .title,
#billboard .text .subhead,
#billboard .text .watch,
#billboard .text .video,
#billboard .text .learn,
#billboard .image { background-repeat:no-repeat; }

#billboard .text .title,
