/****************************************
 *  Header
 ****************************************/

#header {
  height: 99px;
  clear: both;
}

h1.header-logo {
  float: left;
  margin: 22px 30px 0 41px;
}

ul.header-navigation {
  float: right;
  list-style: none;
  margin: 30px 26px 0 0;
  padding: 0;
}

  ul.header-navigation li {
    float: left;
    list-style: none;
    margin: 0;
    padding: 0;
    font-size: 16px;
    line-height: 18px;
  }

    ul.header-navigation li a {
      display: block;
      padding: 11px 17px 0 17px;
      height: 30px;
    }

  ul.header-navigation li.active {
    background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/layout/header-navigation-hover-left.png);
    background-position: center left;
    background-repeat: no-repeat;
  }

    ul.header-navigation li.active a {
      background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/layout/header-navigation-hover-right.png);
      background-position: center right;
      background-repeat: no-repeat;
      color: #404040;
      text-shadow: #fff 0 1px 0;
    }

      ul.header-navigation li.active a:hover {
        text-decoration: none;
      }

/****************************************
