.section-website-records .website-record.status-broken .info { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/icons/alert-24x24.png); }
.section-website-records .website-record.status-deactivated .info { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/icons/red-x-24x24.png); }
.section-website-records .website-record.status-inactive .info { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/icons-custom/gray-x-24x24.png); }

.section-website-records .website-record .options {
