/****************************************
 *  Modules
 ****************************************/

.module {
  clear: both;
}

.module .content {
  clear: both;
}

/* Modules: Progress box */

.module-progress-box {
  background-color: #f5f7f7;
  border: 1px solid #dfe6e8;
  border-top: none;
  padding: 22px 25px;
  color: #666;
	position: relative; /* make sure absolute positioned children respect their parent */
  -webkit-border-bottom-right-radius: 5px;
  -webkit-border-bottom-left-radius: 5px;
  -moz-border-radius-bottomright: 5px;
  -moz-border-radius-bottomleft: 5px;
  border-bottom-right-radius: 5px;
  border-bottom-left-radius: 5px;}

.module-progress-box .main {
	float: left;
	width: 650px;
}

.module-progress-box .secondary {
	float: right;
	width: 190px;
}

.process-help-text {
  position: absolute;
	font-size: 12px;
	right: 0;
	bottom: 0;
	text-align: right;
	margin: 25px;
	color: #b6bcbf;
}

/* Modules: Progress bar */

.module-progress-bar {
  color: #A1B9C0;
  font-weight: bold;
  margin: 0;
	padding: 0;
  height: 41px;
  float: left;
}

.module-progress-bar .inner {
  float: left;
}

.module-progress-bar .divider {
  float: left;
  width: 12px;
  height: 40px;
  background-repeat: no-repeat;
  background-position: top left;
	border-top: 1px solid #dfe6e8;
	border-bottom: 1px solid #dfe6e8;
}

.module-progress-bar .step {
	float: left;
  height: 31px;
  line-height: 20px;
  font-weight: bold;
  color: #a8b8bf;
  text-align: center;
  padding: 9px 0 0 0;
  margin: 0;
  background: transparent url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/progress-bar-tab-background-off.png) top center repeat-x;
  display: block;
	border-top: 1px solid #dfe6e8;
}

.module-progress-bar .step-first {
  -webkit-border-top-left-radius: 5px;
  -moz-border-radius-topleft: 5px;
  border-top-left-radius: 5px;
	border-left: 1px solid #dfe6e8;
	padding-left: 6px;
}

.module-progress-bar .step-last {
	-moz-border-radius-topright: 5px;
	-webkit-border-top-right-radius: 5px;
	border-top-right-radius: 5px;
	border-right: 1px solid #dfe6e8;
	padding-right: 6px;
}

.module-progress-bar.standalone .step-first {
	-moz-border-radius-bottomleft: 5px;
	-webkit-border-bottom-left-radius: 5px;
	border-bottom-left-radius: 5px;
}

.module-progress-bar.standalone .step-last {
	-moz-border-radius-bottomright: 5px;
	-webkit-border-bottom-right-radius: 5px;
	border-bottom-right-radius: 5px;
}

.module-progress-bar .step-1 { width: 214px; }
.module-progress-bar .step-2 { width: 192px; }
.module-progress-bar .step-3 { width: 226px; }
.module-progress-bar .step-4 { width: 266px; }

.module-progress-bar .active {
  color: #5b666b;
  background: #f2f2f2;
}

.module-progress-bar .active {
  background: transparent url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/progress-bar-tab-background-on.png) top center repeat-x;
}

.module-progress-bar .divider-off-off { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/progress-bar-divider-off-off.png); }
.module-progress-bar .divider-off-on { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/progress-bar-divider-off-on.png); }
.module-progress-bar .divider-on-off { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/progress-bar-divider-on-off.png); }

.module-progress-bar.standalone {
	margin-bottom: 30px;
}

.module-progress-bar.standalone .step {
  height: 29px;
  border-bottom: 1px solid #c2c8ca;
}

.module-progress-bar.standalone .step-first {
  border-left-color: #d0d7d9;
}

.module-progress-bar.standalone .step-last {
  border-right-color: #d0d7d9;
}

.module-progress-bar.standalone .divider {
  height: 38px;
  border-bottom: 1px solid #c2c8ca;
}

/* Modules: Alert messages */

#section-page-alerts {
  margin-bottom: 20px;
}

.alert {
  position: relative;
  margin: 0 0 20px 0;
  padding: 0px 20px 2px;
  border: 1px solid #f2e2c2;
  background: #fffde5;
  color: #8c532a;
  font-size: 13px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
}

  .alert h2,
	.alert h3,
	.alert h4,
	.alert h5,
	.alert p,
	.alert ul,
	.alert ol {
	  margin: 15px 0;
	  color: inherit !important;
	}

	.alert p {
	  line-height: 18px;
	}

.alert-warning {
  background: #fff8f2;
  border-color: #f2ac79;
  color: #cc6114;
}

.alert-error {
  background: #fff2f2;
  border-color: #d58080;
  color: #cc2929;
}

.alert-success {
  background: #f9fff2;
  border-color: #97bf60;
  color: #528c00;
}

.alert-pointer {
  position: absolute;
  width: 14px;
  height: 11px;
  background: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/alert-pointer-up.png) no-repeat 0 0;
}

div.attention-below {

    display: block;
    position: absolute;
    bottom: -85px;
    left: 360px;
    width: 89px;
    height: 103px;
    background: url('/web/20101223174841im_/http://www.cloudflare.com/images/ui/alert-attention-below.png') no-repeat 0 0;

}

.alert-pointer-up-left {
  top: -11px;
  left: 20px;
}

.alert-pointer-up-right {
  top: -11px;
  right: 20px;
}

.alert-warning .alert-pointer { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/alert-pointer-up-warning.png); }
.alert-error .alert-pointer { background-image: url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/alert-pointer-up-error.png); }

/* Modules: Tabs */

.module-tabs ul.tabs {
	padding: 5px 5px 0 5px;
