/* Modules: dropdown menu */

.dropdownMenu {
  float: left;
  position: relative;
  z-index: 1;
}

.dropdownMenu ul {
  display: none;
	list-style: none;
  margin: 0;
  padding: 6px 0;
  position: absolute;
  background-color: #f5f7f7;
  border: 1px solid #dfe6e8;
  min-width: 100px;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	border-radius: 4px;
  z-index: 1;
}

.dropdownMenu li {
  margin: 0;
  padding: 0;
	white-space: nowrap;
	font-weight: normal;
  font-size: 11px;
  line-height: 13px;
}

.dropdownMenu li a {
	padding: 5px 20px 4px 10px;
	display: block;
}

.dropdownMenu-active,
.dropdownMenu-active ul {
  display: block;
  z-index: 100;
}

.dropdownMenu li a:hover {
	background-color: #007ed9;
	color: white;
  text-decoration: none;
}

.dropdownMenu li a.critical:hover {
	background-color: #cc2929;
}

.dropdownMenu li.dropdownMenu-separator {
  margin: 5px 12px;
  height: 1px;
  background: #dfe6e8;
}

.dropdownMenu a.dropdownMenu-button {
  display: block;
  z-index: 1;
  margin: 0 0 1px 0;
	padding: 4px 26px 0 7px;
	height: 18px;
  border-width: 1px;
  border-style: solid;
  border-color: #dfe6e8 #dce2e4 #c2c8ca;
	font-size: 12px;
	line-height: 14px;
	font-weight: bold;
	white-space: nowrap;
  background: #fff url(/web/20101223174841im_/http://www.cloudflare.com/images/ui/dropdownMenu-bg.png) no-repeat 100% 0;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	outline: none;
}

.dropdownMenu a.dropdownMenu-button:hover,
.dropdownMenu a.dropdownMenu-button:focus {
  text-decoration: none;
  color: #404040;
  border-color: #d0d7d9 #ccd3d5 #abb1b3;
}

.dropdownMenu-active a.dropdownMenu-button:hover,
.dropdownMenu-active a.dropdownMenu-button:focus {
  border-color: #dfe6e8 #dce2e4 #c2c8ca;
}

.dropdownMenu-active a.dropdownMenu-button {
  color: #404040;
  text-decoration: none;
  background-position: 100% -22px;
}

.dropdownMenu-disabled a:hover,
.dropdownMenu-disabled:hover a.dropdownMenu-button {
  color: #b6bcbf;
  cursor: default;
  background-position: 100% -44px;
}

.dropdownMenu-disabled:hover ul {
  display: none;
}

.dropdownMenu-disabled a.dropdownMenu-button,
.dropdownMenu-disabled a.dropdownMenu-button:hover,
.dropdownMenu-disabled a.dropdownMenu-button:focus {
  color: #b6bcbf !important;
