/* Use the following HTML
<div class="result-circle checkmark-circle">
  <div class="background"></div>
  <div class="inner"></div>
</div>
*/
.checkmark-circle {
  width: 50px;
  height: 50px;
  position: relative;
  display: inline-block;
  vertical-align: top; }
  .checkmark-circle .background {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: #94C03B;
    position: absolute; }
  .checkmark-circle .inner:before {
    opacity: 1;
    height: 25px;
    width: 12.5px;
    transform-origin: left top;
    border-right: 5px solid white;
    border-top: 5px solid white;
    content: '';
    left: 8.33333px;
    top: 25px;
    position: absolute;
    transform: rotate(225deg) scaleX(-1); }

.expand-circle {
  width: 50px;
  height: 50px;
  position: relative;
  display: inline-block; }
  .expand-circle .background {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: #2f7bbf;
    position: absolute; }
  .expand-circle .plus:before {
    opacity: 1;
    font-size: 3em;
    color: #fff;
    width: 50px;
    height: 50px;
    content: "+";
    text-align: center;
    transition: opacity 500ms ease-in;
    position: absolute;
    line-height: 2.8em; }
  .expand-circle .plus.expanded:before {
    content: "-"; }

.x-red-circle {
  width: 50px;
  height: 50px;
  position: relative;
  display: inline-block; }
  .x-red-circle .background {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    position: absolute;
    background: #bb4b43; }
  .x-red-circle .inner:before {
    opacity: 1;
    font-size: 3em;
    color: #fff;
    width: 50px;
    height: 50px;
    content: "x";
    position: absolute;
    line-height: 2.8em;
    left: 0; }

