/* Profile
/*
/*****************************************************************************/

#profile {
  margin: 3em auto 0 auto;
  width: 60em;
}

/*****************************************************************************/
/*
/* Login
/*
/*****************************************************************************/

#login {
  width: 31em;
}

/*****************************************************************************/
/*
/* Forgot Password
/*
/*****************************************************************************/

#forgot_password {
  width: 31em;
}

/*****************************************************************************/
/*
/* Repo Menu
/*
/*****************************************************************************/

#repo_menu {
  background: #eaeaea url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/bg.png) 0 0 repeat-x;
  text-align: left;
  overflow: hidden;
}

  #repo_menu ul {
    overflow: hidden;
    background: #f4f4f4 url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/tab.png) 0 0 repeat-x;
    border-bottom: 3px solid white;
  }

    #repo_menu ul li {
      background: #f4f4f4 url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/tab.png) 0 0 repeat-x;
      float: left;
      list-style-type: none;
      height: 1.6em;
      padding-top: .4em;
      border-right: 2px solid #eee;
      position: relative;
    }

    #repo_menu ul li:hover {
      background: #f4f4f4 url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/tab_hover.png) 0 0 repeat-x;
    }

    #repo_menu ul li.active {
      background: white url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/tab_active.png) 0 0 repeat-x;
    }

      #repo_menu ul li a {
        padding: 1em 1.25em .3em 1.25em;
        color: #979797;
        font-size: 110%;
      }

      #repo_menu ul li a:hover {
        color: black;
        text-decoration: none;
      }

    #repo_menu ul li.active a {
      color: black;
    }

#triangle {
  background: url(/web/20080906042852im_/http://github.com/images/modules/repo_menu/triangle.gif);
  width: 10px;
  height: 10px;
}

/*****************************************************************************/
/*
/* Repo Sub Menu
/*
/*****************************************************************************/

#repo_sub_menu {
  height: 2.099em;
  background: #dadada url(/web/20080906042852im_/http://github.com/images/modules/repo_sub_menu/bg.png) 0 100% repeat-x;
  margin-bottom: 1.5em;
  line-height: 1.5;
}

  #repo_sub_menu .site {
    padding: .4em 1em 0 1em;
  }

    #repo_sub_menu ul {
      list-style: none;
    }

    #repo_sub_menu li {
      float: left;
      position: relative;
    }

      #repo_sub_menu li ul {
        display: none;
        position: absolute;
        z-index: 1000;
        top: 1em;
        left: 0;
        background-color: #d0d0d0;
        margin-left: 1.7em;
        border-left: 1px solid #cfcfcf;
        border-right: 1px solid #cfcfcf;
        border-bottom: 1px solid #cfcfcf;
        padding: .3em;
      }

      #repo_sub_menu li > ul {
        top: auto;
        left: auto;
      }

        #repo_sub_menu li ul li {
          top: auto;
          left: auto;
          float: none;
          min-width: 6em;
        }

          #repo_sub_menu li ul li a {
            margin-left: 0;
            padding: .1em .3em;
            display: block;
          }

      #repo_sub_menu li:hover ul {
        display: block;
      }

    #repo_sub_menu a {
      padding: .3em;
      margin-left: 2em;
      color: black;
    }

    #repo_sub_menu a:hover {
      text-decoration: none;
      background-color: #bbb;
    }

    #repo_sub_menu a.active {
      background-color: #eee;
    }

