/*****************************************************************************/
/*
/* Commit
/*
/*****************************************************************************/

#commit {
  overflow: hidden;
}

  #commit .group {
    border-top: 1px solid #bedce7; /* blue */
  }

  #commit .separator {
    padding-top: 1em;
  }

  #commit .envelope {
    border-bottom: 1px solid #bedce7; /* blue */
