  }

  #commit .envelope.selected {
    background: #fffeeb !important;
  }

    #commit .envelope.selected .machine span {
      border-bottom: 1px dotted #4183c4; /* blue */
    }

    #commit.single_commit .envelope .machine span {
      border-bottom: 1px dotted #4183c4; /* blue */
    }

