    }

  #message .actions {
    float: left;
    width: 13em;
    margin-bottom: 2em;
  }

    #message .actions h1 {
      color: white;
    }

    #message .compose {
      border-bottom: 1px solid #ddd;
      font-size: 120%;
      padding-bottom: .5em;
      margin: .3em 0 1em 0;
    }

      #message .actions .compose p a {
