  #tour .signup {
    text-align: center;
    margin-top: 4em;
  }

/*****************************************************************************/
/*
/* Pagination
/*
/*****************************************************************************/

.pagination {
  padding: .3em;
  margin: .3em;
}

.pagination a {
  padding: .1em .3em;
  margin: .2em;
  border: 1px solid #aaaadd;
  text-decoration: none;
  color: #369;
}

.pagination a:hover, .pagination a:active {
  border: 1px solid #369;
  color: #000;
}

.pagination span.current {
  padding: .1em .3em;
  margin: .2em;
  border: 1px solid #369;
  font-weight: bold;
  background-color: #369;
  color: #FFF;
}

.pagination span.disabled {
  padding: .1em .3em;
  margin: .2em;
  border: 1px solid #eee;
  color: #ddd;
}

/*****************************************************************************/
/*
/* Commit Comments
/*
/*****************************************************************************/
#commit_comments {
}

  #commit_comments h1 {
    margin-bottom: 0.1em;
  }

  #commit_comments .inner {
    height: 22em;
    overflow: auto;
  }

  #commit_comments .inner form {
    padding-bottom: 1em;
  }

  #commit_comments code, #comments code {
    font-size: 0.9em;
    background: #000;
    padding: 0.1em;
    color: #fff;
    border: 1px solid #eee;
    font-family: Monaco, "Courier New", monospace;
  }

  #commit_comments ul {
    list-style-type: none;
  }

    #commit_comments ul li.nopad {
      padding: 0;
    }

    #commit_comments ul li {
      border-top: 1px solid #ddd;
      padding-top: 1em;
    }

    #commit_comments ul li.first {
      border-top: none;
    }

      #commit_comments ul li p {
        padding-left: 1em;
        font-family: Monaco, "Courier New", monospace;
        font-size: 90%;
      }

      #commit_comments ul li textarea { 
        font-family: Monaco, "Courier New", monospace;
        font-size: 92%;
      }

/*****************************************************************************/
/*
/* Downloads
/*
/*****************************************************************************/

ul#downloads {
  list-style-type: none;
  text-align: center;
}

  ul#downloads li {
    display: inline;
  }

#other_downloads {
  border-top: 1px solid #ddd;
  padding-top: 0.5em;
}

  #other_downloads ul {
    list-style-type: none;
    width: 8em;
    float: left;
    clear: right;
  }



/*****************************************************************************/
/*
/* Popup
/*
/*****************************************************************************/

.popup .standard_form {
  margin-top: 0;
  padding: 0;
  border: 0;
  width: 35em;
}


  .popup .standard_form .clean_list {
    list-style-type: none;
  }

  .popup .standard_form h1 {
    background: #ddd;
    padding: 0.5em 0.6em;
  }

    .popup .standard_form h1 .links {
      font-size: 0.6em;
      float: right;
      font-weight: none;
      margin-top: -0.4em;
    }

    .popup .standard_form h1 .links a {
      font-size: 1em;
    }

  .popup .standard_form .pull_request_inside, .popup .standard_form .inner {
    padding: 0 1em 0.5em 1em;
  }

  .popup .standard_form .actions {
    border-top: 1px solid #ddd;
    padding: 0.5em;
  }

    .popup .standard_form .actions label {
      clear: none;
      display: inline;
      padding-left: 1em;
    }


#facebox .popup .content, #facebox .popup .loading {
  padding: 0.5em;
}

#facebox .nopad .content, #facebox .popup .nopad {
  padding: 0;
}

#facebox .footer {
  padding: 0.3em 1em;
}

#facebox .tip {
  width: 500px;
  padding: 1em;
}

#facebox .body {
  padding: 0;
}

/*****************************************************************************/
/*
/* Tooltips
/*
/*****************************************************************************/

.tool-tip {
  color: #fff;
  width: 350px;
  z-index: 13000;
}

.tool-title {
  font-weight: bold;
  font-size: 11px;
  margin: 0;
  color: #9FD4FF;
  padding: 8px 8px 4px;
  background: url(/web/20080906042852im_/http://github.com/images/modules/tooltip/bubble.png) top left;
}

.tool-text {
  font-size: 11px;
  padding: 4px 8px 8px;
  background: url(/web/20080906042852im_/http://github.com/images/modules/tooltip/bubble.png) bottom right;
}

.custom-tip {
  color: #000;
  width: 130px;
  z-index: 13000;
}

.custom-title {
  font-weight: bold;
  font-size: 11px;
  margin: 0;
  color: #3E4F14;
  padding: 8px 8px 4px;
  background: #C3DF7D;
  border-bottom: 1px solid #B5CF74;
}

.custom-text {
  font-size: 11px;
  padding: 4px 8px 8px;
  background: #CFDFA7;
}


