.colorpicker_rgb_r{top:52px;left:212px;}
.colorpicker_rgb_g{top:82px;left:212px;}
.colorpicker_rgb_b{top:112px;left:212px;}
.colorpicker_hsb_h{top:52px;left:282px;}
.colorpicker_hsb_s{top:82px;left:282px;}
.colorpicker_hsb_b{top:112px;left:282px;}
.colorpicker_submit{position:absolute;width:22px;height:22px;left:322px;top:142px;overflow:hidden;}
.colorpicker_focus{background-position:center;}
.colorpicker_hex.colorpicker_focus{background-position:bottom;}
.colorpicker_submit.colorpicker_focus{background-position:bottom;}
.colorpicker_slider{background-position:bottom;}
.userbox{float:right;height:39px;padding-left:6px;font-size:12px;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/header/userbox/bar-left.png) 0 0 no-repeat;}
.userbox .inner{height:29px;padding:10px 12px 0 6px;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/header/userbox/bar-right.png) 100% 0 no-repeat;}
#header .userbox a{color:#4183c4;font-weight:bold;text-decoration:none;}
#header .userbox ul.usernav a:hover,#header .userbox a.name:hover{text-decoration:underline;}
.userbox .avatarname{display:inline;}
.userbox .avatarname img{margin-top:-3px;margin-right:3px;vertical-align:middle;border:1px solid #fff;}
#header .userbox .avatarname a{color:#000;}
ul.usernav{display:inline;margin:0;}
ul.usernav li{list-style-type:none;display:inline;margin:0;padding:0 0 0 15px;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/header/userbox/bar_separator.gif) 5px 0 no-repeat;}
ul.usernav li a{text-shadow:#fff 1px 1px 1px;}
#header ul.usernav a.unread_count,#header ul.usernav a.unread_count:hover{display:inline-block;margin-left:2px;font-size:10px;padding:1px 5px;background:#ddd;color:#999;font-weight:bold;text-shadow:none;text-decoration:none;-webkit-border-radius:5px;-moz-border-radius:5px;}
#header ul.usernav a.unread_count.new_messages{background-color:#4183c4;color:#fff;}
.topsearch{float:right;clear:right;margin-top:15px;width:500px;}
.topsearch form,.topsearch ul.nav{float:right;}
.topsearch form input.button{display:none;}
.topsearch form .advanced-search{display:inline-block;*display:none;width:16px;height:16px;text-indent:-9999px;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/header/advanced_search_icon.png) 0 0 no-repeat;opacity:.2;}
.topsearch form .advanced-search:hover{opacity:.5;}
.topsearch form input.search{font-size:16px;width:180px;}
.topsearch form input.search.notnative{width:149px;height:16px;padding:4px 10px 2px 21px;font-size:12px;border:none;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/header/search_field.gif) 0 -22px no-repeat;}
.topsearch form input.search.notnative.placeholder{background-position:0 0;}
ul.nav{margin:1px 0 0 0;white-space:nowrap;}
ul.nav li{list-style-type:none;display:inline;margin:0 15px 0 0;font-size:12px;}
#header ul.nav li a{color:#4183c4;font-weight:bold;}
#header ul.nav li a:hover{text-decoration:underline;}
ul.nav.logged_out{margin-top:15px;}
ul.nav.logged_out.nosearch{margin-top:25px;}
ul.nav.logged_out li{font-size:14px;margin-right:0;margin-left:20px;}
#header ul.nav.logged_out li a{font-weight:normal;color:#666;}
#header ul.nav.logged_out li.pricing a{font-weight:bold;color:#333;}
#site_alert{background-color:#fcfcfc;}
#site_alert p{text-align:center;font-weight:bold;color:#fff;background:#000;padding:5px 0;}
.newsfeed .alert{padding:10px 25px 10px 0;font-size:12px;color:#333;border-bottom:1px solid #ddd;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/dashboard/news/commit.png) 100% 10px no-repeat;}
.newsfeed .alert:first-child{border-top:1px solid #ddd;}
.newsfeed .alert.push{height:auto;}
.newsfeed .alert .title{font-weight:bold;font-size:14px;color:#000;}
.newsfeed .alert .title abbr{font-size:12px;font-weight:normal;color:#999;}
