.pagehead{position:relative;overflow:hidden;margin:-30px 0 0 -25px;width:920px;padding:20px 25px 0 25px;background:url(/web/20110223132127im_/http://assets3.github.com/images/modules/pagehead/background-white.png) 0 0 no-repeat;}
.pagehead.mine{background-image:url(/web/20110223132127im_/http://assets3.github.com/images/modules/pagehead/background-yellow.png);}
.pagehead h1{margin:0 0 10px 0;height:54px;line-height:54px;font-size:30px;font-weight:normal;letter-spacing:-1px;text-shadow:1px 1px 1px #fff;color:#495961;}
.pagehead h1 a{color:#495961;}
