styles:

 @media screen and (max-device-width: 480px) {
     * { margin: 0; border: 0; padding: 0; }
     body { -webkit-text-size-adjust: none; }

     div {
         clear: both !important;
         float: none !important;
         width: 320px !important;
     }

     #wrapper { margin: 0; }
     #header { margin: 0; min-width: 320px !important; padding-top: 0; padding-bottom: 5px; }
         #headerWrapper { margin-top: 0; }
     #pinterestNav { left: 0 !important; margin-left: 0 !important; position: relative !important; }
         .nav, #SocialShare, .repin-button, #djDebugToolbarHandle, #BackToTop { display: none !important; }
     .fixedWidthContainer { margin: 0 !important; }

     #individual #article { min-width: 320px !important; margin-left: 0 !important; padding-top: 90px !important; }

     #introductionRight, .profilebox img { display: none; }

     .columnContainer { margin-top: 25px !important; }

     .pin { width: 300px !important; padding: 10px !important; margin-bottom: 10px !important; }
     .pin.pinBoard { width: 320px !important; }
         .pin .image { margin: 0 auto; }
         .pin .image img { max-width: 300px !important; margin: 0 auto; }
     #boardMetadata { width: 300px !important; padding: 10px !important; display: none !important; margin-bottom: 10px !important; }

     .profilebox { top: 50px !important; }

     .pinOrigin { width: 300px !important; padding: 10px !important; margin: 5px -10px -10px; }
         .pinOrigin p { min-height: 28px; }
     .pinComments { margin: 10px -10px -10px; border-top: 1px solid #E7E7E7; }
     .pinComments div { padding-left: 10px; padding-right: 10px; width: 300px !important; border-top: 0 solid #E7E7E7; }

     #sortable li { width: 320px !important; margin-right: 0; margin-bottom: 10px !important; }

     #boardIntroduction { width: 300px !important; padding: 10px !important; }
     #boardIntroFilters { height: auto; padding-top: 5px; padding-bottom: 5px; }

     #feed_activity_pin { padding-left: 0 !important; padding-right: 0 !important; width: 320px !important; }
     #recent_activity_holder { width: 320px !important; }
     #recent_activity_holder .story { overflow: hidden; }
     #recent_activity_holder .story img { position: absolute; }
     .activity_details { float: left !important; width: 255px !important; margin-left: 50px; }
}

