styles:

 @media screen and (max-device-width: 480px) {
     #introductionRight, .profilebox img {display: none;}
     .profilebox {top: 50px !important;}
     .pinOrigin {width: 300px !important; padding: 10px !important; margin: 5px -10px -10px;}
         .pinOrigin p {min-height: 28px;}
     .pinComments {margin: 10px -10px -10px; border-top: 1px solid #E7E7E7;}
     .pinComments div {padding-left: 10px; padding-right: 10px; width: 300px !important; border-top: 0 solid #E7E7E7;}
     .sortable li {width: 320px !important; margin-right: 0; margin-bottom: 10px !important;}
     #boardIntroduction {width: 300px !important; padding: 10px !important;}
     #boardIntroFilters {height: auto; padding-top: 5px; padding-bottom: 5px;}
     #article .feed {display: none;}
     #recent_activity_holder {width: 320px !important;}
     #recent_activity_holder .story {overflow: hidden;}
     #recent_activity_holder .story img {position: absolute;}
     .activity_details {float: left !important; width: 255px !important; margin-left: 50px;}
}

