/*MODALS
---------------------------------------------- */
.overlay-container {position: fixed; z-index: 99999; width: 100%; height: 100%; background: rgba(255,255,255,.7);}

.modal {position: relative; width: 500px; margin: 150px auto 0; padding: 15px 20px 10px; background: #fff; border: 5px solid #ecebe9; border-radius: 10px; -moz-border-radius: 10px; -webkit-border-radius: 10px; box-shadow: 0px 1px 32px rgba(0,0,0,.1); -moz-box-shadow: 0px 1px 32px rgba(0,0,0,.1); -webkit-box-shadow: 0px 1px 32px rgba(0,0,0,.1);}
	.modal .close-button {position: absolute; right: 20px; display: block; padding: 9px 0 5px; font-size: 14px; text-decoration: underline; color: #9294a7;}
	.modal .close-button:hover {color: #555; cursor: pointer;}
	.modal h2 {margin-top: 5px; padding: 0; font-size: 22px; font-weight: bold;}
	.modal form {margin: 5px 0 0;}
	.modal form p {clear: both; margin: 0; padding: 9px 0; overflow: hidden; border-bottom: 1px solid #eee;}
		.no-border-button {border-bottom: 0 !important;}
		.button-p {padding-bottom: 10px !important;}
	.modal label {float: left; display: block; width: 160px; padding-top: 7px; font-size: 14px; font-weight: bold; text-align: right; color: #666;}

	.modal input[type=text],
	.modal textarea {float: right; width: 311px; padding: 5px 8px; font-size: 14px; line-height: 1.2em; color: #777; font-family: "Helvetica Neue", Helvetica, Arial, sans-serif; background-color: #fff; border: 1px solid #bbb; border-top-color: #ccc; border-bottom-color: #aaa; box-shadow: #e7e7e7 0 1px; -moz-box-shadow: #e7e7e7 0 1px; -webkit-box-shadow: #e7e7e7 0 1px;}
	.modal textarea {padding: 8px 8px; max-width: 311px; height: 120px;}
	.modal .button {display: block; padding: 14px 0; text-align: center; font-size: 18px;}

	/*Search Autocomplete */
	#SearchAutocompleteHolder .ui-autocomplete {width: 190px; padding: 0; font-size: 13px; background: #fff; border: 1px solid #ccc; border-top-width: 0; border-bottom-width: 0; box-shadow: 0 2px 2px rgba(0,0,0,.1); -moz-box-shadow: 0 2px 2px rgba(0,0,0,.1); -webkit-box-shadow: 0 2px 2px rgba(0,0,0,.1);}
		#SearchAutocompleteHolder li:last-child {border-bottom: 1px solid #ccc;}
		#SearchAutocompleteHolder .ui-autocomplete-category {display: inline-block; width: 180px; padding: 3px 5px; font-size: 12px; font-weight: bold; color: #4d4d4d; text-shadow: 0 1px 0 white; background: #ebebeb; border-top: 1px solid #dadada; border-bottom: 1px solid #e2e2e2;}
		#SearchAutocompleteHolder .ui-menu-item {padding: 0 !important;}
		#SearchAutocompleteHolder .ui-menu-item:last-child {background: #eee; border-top: 1px solid #dadada;}
		#SearchAutocompleteHolder .ui-menu-item a {display: block; min-height: 28px; padding: 3px 6px !important; color: #666;}
			#SearchAutocompleteHolder a.ui-corner-all {padding: 3px 6px !important;}
			#SearchAutocompleteHolder .ui-menu-item a:hover {padding: 3px 6px !important; color: #fff !important; cursor: pointer; background: #1388e4; border-bottom: 0 !important;}
			#SearchAutocompleteHolder .ui-menu-item a:active {padding: 3px 6px !important; background: #1388e4; color: #fff !important;}
			#SearchAutocompleteHolder .ui-menu-item span {font-size: 12px;}
			#SearchAutocompleteHolder .ui-menu-item img {float: left; margin-top: 2px; margin-right: 5px;}
			#SearchAutocompleteHolder .ui-menu-item h5 {font-size: 13px; font-weight: bold; color: #353535;}
		#SearchAutocompleteHolder .ui-menu-item:hover h5 {color: #fff;}
.popup .bookmarklet {top: 5px; left: 20px; margin: 10px; padding-bottom: 20px;}
